INSERT INTO `blog_posts_seo` (`postID`, `postTitle`, `postSlug`, `postDesc`, `postCont`, `postDate`, `postTags`, `postImage`) VALUES
(71, 'Xếp hạng và Phi Phong', 'xep-hang-va-phi-phong', NULL, '<p>Phi Phong l&agrave; trang bị c&oacute; những thuộc t&iacute;nh ma ph&aacute;p nghịch thi&ecirc;n, sở hữu bề ngo&agrave;i lấp l&aacute;nh, bộc lộ sự uy nghi&ecirc;m, oai dũng của người mặc. Sau 00h15 H&agrave;ng Ng&agrave;y, hệ thống cập nhật xếp hạng Vinh Dự, nếu đồng đạo lọt v&agrave;o Top bảng xếp hạng th&igrave; c&oacute; thể mua Phi Phong với cấp độ tương ứng. C&oacute; tất cả 03 loại Vinh Dự.</p>\r\n\r\n<h1 style=\"text-align:left\">Thời gian xếp hạng</h1>\r\n\r\n<div class=\"ContentH4\" style=\"padding:0px; text-align:left\">\r\n<h2 style=\"text-align:justify\"><strong>Thời gian:</strong>&nbsp;<strong>00:15 H&agrave;ng Ng&agrave;y</strong></h2>\r\n</div>\r\n\r\n<h1 style=\"text-align:left\">C&oacute; 3 loại Vinh Dự</h1>\r\n\r\n<div class=\"ContentH4\" style=\"padding:0px; text-align:left\">\r\n<table align=\"center\" border=\"0\" cellspacing=\"0\">\r\n	<thead>\r\n		<tr>\r\n			<th style=\"text-align:center\"><strong>Vinh dự T&agrave;i Ph&uacute;</strong></th>\r\n			<th style=\"text-align:center\"><strong>Vinh dự Thủ Lĩnh</strong></th>\r\n			<th style=\"text-align:center\"><strong>Vinh dự V&otilde; L&acirc;m</strong></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h4>Khi cường h&oacute;a trang bị sẽ gia tăng điểm t&agrave;i ph&uacute;, cường h&oacute;a trang bị th&ocirc;ng qua&nbsp;<strong>NPC D&atilde; Luyện Đại Sư</strong></h4>\r\n\r\n			<h4>C&aacute;c bạn chỉ cần đạt đủ điểm t&agrave;i ph&uacute; l&agrave; sẽ nhận được danh hiệu mốc đ&oacute;</h4>\r\n\r\n			<h4>Vinh Dự T&agrave;i Ph&uacute; l&agrave; ti&ecirc;u chuẩn để đ&aacute;nh gi&aacute; gi&aacute; trị của nh&acirc;n vật. Cường h&oacute;a trang bị, trang bị ngoại trang, phi phong sẽ gi&uacute;p tăng điểm T&agrave;i Ph&uacute;.</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Đ&acirc;y l&agrave; ti&ecirc;u ch&iacute; để đ&aacute;nh gi&aacute; khả năng l&atilde;nh đạo, thu h&uacute;t bằng hữu của c&aacute;c thủ lĩnh. Nh&acirc;n vật đảm nhận vai tr&ograve; đội trưởng khi tham gia c&aacute;c t&iacute;nh năng sẽ được tăng th&ecirc;m Vinh Dự Thủ Lĩnh.</h4>\r\n\r\n			<h4>C&aacute;c th&agrave;nh vi&ecirc;n Gia Tộc &amp; Bang hội tham gia tr&ograve; chơi th&igrave; Tộc Trưởng v&agrave; Bang Chủ sẽ được t&iacute;ch lũy th&ecirc;m điểm Vinh Dự Thủ Lĩnh.</h4>\r\n\r\n			<h4><strong>Lưu &yacute;:</strong>&nbsp;Vinh Dự Thủ Lĩnh sẽ suy giảm 20% v&agrave;o ng&agrave;y 28 mỗi th&aacute;ng.</h4>\r\n\r\n			<h4>Muốn luyện th&agrave;nh đẳng cấp Vinh Dự Thủ Lĩnh phải đ&aacute;p ứng đồng thời y&ecirc;u Vinh Dự</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Vinh Dự V&otilde; L&acirc;m l&agrave; ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; mức độ tiếng tăm của nh&acirc;n vật trong c&otilde;i Kiếm Thế. Tham gia c&aacute;c t&iacute;nh năng trong game như Thi Đấu M&ocirc;n Ph&aacute;i, V&otilde; L&acirc;m Li&ecirc;n Đấu, Chiến Trường Tống Kim (Mỗi tuần c&oacute; tối đa 14 trận được nhận, thứ hạng c&agrave;ng cao điểm nhận c&agrave;ng nhiều),... sẽ nhận điểm Vinh Dự V&otilde; L&acirc;m.</h4>\r\n\r\n			<h4><strong>Lưu &yacute;:</strong>&nbsp;Vinh Dự V&otilde; L&acirc;m sẽ suy giảm 20% v&agrave;o ng&agrave;y 28 mỗi th&aacute;ng.</h4>\r\n\r\n			<h4>Muốn luyện th&agrave;nh đẳng cấp Vinh Dự V&otilde; L&acirc;m phải đ&aacute;p ứng đồng thời y&ecirc;u Vinh Dự</h4>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<h1 style=\"text-align:left\">Bảng Vinh Dự<a id=\"bangvinhdu\" name=\"bangvinhdu\"></a></h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Để xem điểm vinh dự c&aacute;c bạn ấn ph&iacute;m&nbsp;<strong>F1</strong>&nbsp;chọn sang tab&nbsp;<strong>Vinh dự</strong>&nbsp;để xem</h2>\r\n\r\n<div class=\"ContentH4\" style=\"padding:0px; text-align:left\">\r\n<p style=\"text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\"><img src=\"http://i.imgur.com/frs1uLu.png\" style=\"border:0px; height:auto\" /></span></span></p>\r\n\r\n<table align=\"center\" border=\"0\" cellspacing=\"0\" style=\"width:100%\">\r\n	<thead>\r\n		<tr>\r\n			<th style=\"text-align:center\"><strong>Phi phong</strong></th>\r\n			<th style=\"text-align:center\"><strong>Vinh dự t&agrave;i ph&ugrave;</strong></th>\r\n			<th style=\"text-align:center\"><strong>Vinh dự v&otilde; l&acirc;m</strong></th>\r\n			<th style=\"text-align:center\"><strong>Vinh dự thủ lĩnh</strong></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 1 - Si&ecirc;u Ph&agrave;m</strong></td>\r\n			<td style=\"text-align:center\">600</td>\r\n			<td style=\"text-align:center\">500</td>\r\n			<td style=\"text-align:center\">600</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 2 - Xuất Trần</strong></td>\r\n			<td style=\"text-align:center\">1.000</td>\r\n			<td style=\"text-align:center\">1.000</td>\r\n			<td style=\"text-align:center\">1.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 3 - Lăng Tuyệt</strong></td>\r\n			<td style=\"text-align:center\">1.500</td>\r\n			<td style=\"text-align:center\">1.500</td>\r\n			<td style=\"text-align:center\">1.500</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 4 - Kinh Thế</strong></td>\r\n			<td style=\"text-align:center\">3.000</td>\r\n			<td style=\"text-align:center\">2.000</td>\r\n			<td style=\"text-align:center\">3.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 5 - Ngự Kh&ocirc;ng</strong></td>\r\n			<td style=\"text-align:center\">6.000</td>\r\n			<td style=\"text-align:center\">3.000</td>\r\n			<td style=\"text-align:center\">6.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 6 - Hỗn Thi&ecirc;n</strong></td>\r\n			<td style=\"text-align:center\">12.000</td>\r\n			<td style=\"text-align:center\">5.000</td>\r\n			<td style=\"text-align:center\">12.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 7 - Sồ Phượng</strong></td>\r\n			<td style=\"text-align:center\">24.000</td>\r\n			<td style=\"text-align:center\">10.000</td>\r\n			<td style=\"text-align:center\">24.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 8 - Tiềm Long</strong></td>\r\n			<td style=\"text-align:center\">60.000</td>\r\n			<td style=\"text-align:center\">15.000</td>\r\n			<td style=\"text-align:center\">60.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 9 - Ch&iacute; T&ocirc;n</strong></td>\r\n			<td style=\"text-align:center\">120.000</td>\r\n			<td style=\"text-align:center\">18.000</td>\r\n			<td style=\"text-align:center\">120.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Cấp 10 - V&ocirc; Song</strong></td>\r\n			<td style=\"text-align:center\">300.000</td>\r\n			<td style=\"text-align:center\">30.000</td>\r\n			<td style=\"text-align:center\">300.000</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<h1 style=\"text-align:left\">C&aacute;ch mua phi phong</h1>\r\n\r\n<div class=\"ContentH4\" style=\"padding:0px; text-align:left\">\r\n<table align=\"center\" border=\"0\" cellspacing=\"0\" style=\"width:100%\">\r\n	<thead>\r\n		<tr>\r\n			<th><strong>C&ocirc;ng cụ</strong></th>\r\n			<th><strong>Đường link</strong></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><img src=\"https://i.imgur.com/lndVpCz.jpg\" /></p>\r\n\r\n			<p style=\"text-align:center\"><strong>NPC TIỀN TRANG</strong></p>\r\n			</td>\r\n			<td>\r\n			<h4>Mua th&ocirc;ng qua NPC Tiền Trang tại c&aacute;c T&acirc;n thủ th&ocirc;n v&agrave; Th&agrave;nh thị</h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><img src=\"https://i.imgur.com/bbfrjML.jpg\" /></p>\r\n\r\n			<p style=\"text-align:center\"><strong>Điều kiện Mua Phi Phong</strong></p>\r\n			</td>\r\n			<td>\r\n			<h4>Khi mua sẽ y&ecirc;u cầu 2 điều kiện để c&oacute; thể mua được:</h4>\r\n\r\n			<h5><strong>Ngũ H&agrave;nh Hồn Thạch:</strong></h5>\r\n\r\n			<h5>V&agrave;&nbsp;<strong>Vinh dự V&otilde; L&acirc;m / T&agrave;i Ph&uacute; hoặc Thủ Lĩnh</strong>, v&agrave;o mỗi&nbsp;<strong>00h00 H&agrave;ng Ng&agrave;y v&agrave; 03h00 S&aacute;ng Thứ 2</strong>&nbsp;hệ thống sẽ cập nhật xếp hạng của người chơi, đạt cấp xếp hạng n&agrave;o sẽ được quyền mua phi phong theo xếp hạng đ&oacute; -&nbsp;<a href=\"#bangvinhdu\">&gt;&gt;&gt; XEM BẢNG ĐIỂM VINH DỰ Y&Ecirc;U CẦU X&Eacute;T CẤP &lt;&lt;&lt;</a></h5>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n', '2023-03-14 21:48:17', NULL, ''),
(70, 'Thi Đấu Môn Phái', 'thi-dau-mon-phai', NULL, '<p>Thi đấu m&ocirc;n ph&aacute;i l&agrave; hoạt động đặc sắc chỉ c&oacute; ri&ecirc;ng tại Kiếm Thế. Đ&acirc;y l&agrave; nơi cao thủ c&aacute;c ph&aacute;i tranh t&agrave;i với đồng m&ocirc;n để gi&agrave;nh lấy danh hiệu T&acirc;n Nh&acirc;n Vương m&ocirc;n ph&aacute;i, được Chưởng m&ocirc;n trao quyền Đại sư huynh - Đại sư tỷ uy danh khắp thi&ecirc;n hạ.</p>\r\n\r\n<h1>Y&ecirc;u cầu tham gia thi đấu</h1>\r\n\r\n<h2>Người chơi đạt cấp 50 trở l&ecirc;n, đ&atilde; gia nhập m&ocirc;n ph&aacute;i.</h2>\r\n\r\n<h2>Người chơi m&ocirc;n ph&aacute;i n&agrave;o, đến Chưởng M&ocirc;n của m&ocirc;n ph&aacute;i đ&oacute; để đăng k&yacute; thi đấu.</h2>\r\n\r\n<h2>Thời gian thi đấu:&nbsp;<strong>Thứ Ba</strong>&nbsp;trong tuần. L&uacute;c&nbsp;<strong>19h30</strong>&nbsp;bắt đầu b&aacute;o danh, l&uacute;c&nbsp;<strong>20h00</strong>&nbsp;bắt đầu thi đấu.</h2>\r\n\r\n<h1>Giới thiệu sơ lược</h1>\r\n\r\n<h2>Thi đấu m&ocirc;n ph&aacute;i chia ra l&agrave;m hai bước:&nbsp;<strong>S&agrave;ng lọc đồng đội</strong>&nbsp;v&agrave;&nbsp;<strong>Đấu tay đ&ocirc;i</strong>.</h2>\r\n\r\n<h2>Trong thi đấu s&agrave;ng loc đồng đội, 16 người đ&aacute;nh bại nhiều đối thủ nhất mới c&oacute; tư c&aacute;ch tham gia v&ograve;ng đấu tay đ&ocirc;i.</h2>\r\n\r\n<h2>V&ograve;ng đấu tay đ&ocirc;i l&agrave; v&ograve;ng đấu loại trực tiếp giữa hai người chơi với nhau. Mỗi nh&aacute;nh c&oacute; 8 người.&nbsp;<strong>Hai người</strong>&nbsp;chiến thắng mỗi nh&aacute;nh sẽ bước v&agrave;o đấu chung kết tranh chức vị T&acirc;n Nh&acirc;n Vương. Người chiến thắng duy nhất l&agrave;&nbsp;<strong>T&acirc;n Nh&acirc;n Vương</strong>&nbsp;của m&ocirc;n ph&aacute;i.</h2>\r\n\r\n<h2>Trong tuần sẽ c&oacute; 1 ng&agrave;y thi dấu v&agrave;o thứ Ba v&agrave;. Người đ&atilde; gi&agrave;nh ng&ocirc;i vị T&acirc;n Nh&acirc;n Vương vẫn c&oacute; thể tiếp tục tham gia để bảo vệ ng&ocirc;i vị của m&igrave;nh.</h2>\r\n\r\n<h2>Trong qu&aacute; tr&igrave;nh thi đấu c&oacute; thời gian nghỉ ngơi. Trong thời gian n&agrave;y c&oacute; hoạt động T&igrave;m Cờ lấy điểm t&iacute;ch lũy. Những người bị loại c&oacute; thể xem người kh&aacute;c thi đấu v&agrave; c&oacute; cơ hội được kinh nghiệm.</h2>\r\n\r\n<h2>Phần thưởng hoạt động gồm : Danh hiệu, danh vọng, huyền tinh, kinh nghiệm &hellip;</h2>\r\n\r\n<h2>Dựa v&agrave;o danh vọng, c&oacute; thể mua trang bị&nbsp;<strong>Y Phục &ndash; Thi đấu m&ocirc;n ph&aacute;i</strong>&nbsp;tại Chưởng M&ocirc;n.</h2>\r\n\r\n<h1>Hướng dẫn chi tiết</h1>\r\n\r\n<h2>Đ&uacute;ng ng&agrave;y v&agrave; giờ quy định, về m&ocirc;n ph&aacute;i của m&igrave;nh, gặp NPC Chưởng M&ocirc;n, chọn d&ograve;ng đối thoại&nbsp;<strong>Hoạt động thi đấu m&ocirc;n ph&aacute;i</strong>, sau đ&oacute; chọn<strong>&nbsp;Ta muốn đến đấu trường m&ocirc;n ph&aacute;i</strong>. Người tham gia sẽ được đưa v&agrave;o bản đồ chờ.</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_liendaumonphai_01.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Sau khi kết th&uacute;c b&aacute;o danh, hệ thống sẽ ngẫu nhi&ecirc;n ph&acirc;n chia người chơi th&agrave;nh từng nh&oacute;m người thi đấu với nhau.</h2>\r\n\r\n<h2>Những người bị chết trong thể thức thi đấu n&agrave;y, c&oacute; thể hồi sinh v&agrave; đấu tiếp.</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_liendaumonphai_02.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Sau khi kết th&uacute;c thể thức thi đấu v&ograve;ng loại nh&oacute;m, hệ thống sẽ chọn ra 16 người c&oacute; số điểm cao nhất để tham gia thi đấu v&ograve;ng loại trực tiếp.</h2>\r\n\r\n<h2>Giai đọan n&agrave;y, hệ thống ph&acirc;n chia người chơi th&agrave;nh 2 nh&aacute;nh, mỗi nh&aacute;nh c&oacute; 8 người, thi đấu tay đ&ocirc;i với nhau, chọn ra người chiến thắng để tiếp tục đấu tay đ&ocirc;i.</h2>\r\n\r\n<h2>C&oacute; thể nhấn ph&iacute;m&nbsp;<strong>~</strong>&nbsp;để xem nh&aacute;nh thi đấu</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_liendaumonphai_03.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ở mỗi nh&aacute;nh thi đấu, sẽ chọn được một người thắng tuyệt đối để v&agrave;o đấu trận chung kết, tranh T&acirc;n Nh&acirc;n Vương.</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_liendaumonphai_04.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Người chiến thắng sẽ được l&ecirc;n đ&agrave;i nhận&nbsp;<strong>Cờ Qu&aacute;n Qu&acirc;n</strong>&nbsp;v&agrave; danh hiệu. Những người đứng xung quanh ch&uacute;c mừng T&acirc;n Nh&acirc;n Vương cũng sẽ nhận được phần thưởng kinh nghiệm.</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_liendaumonphai_05.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Trong khi thi đấu sẽ c&oacute; những khoảng thời gian nghỉ ngơi. Trong thời gian n&agrave;y, người chơi c&oacute; thể tham gia hoạt động&nbsp;<strong>T&igrave;m Cờ</strong>&nbsp;để kiếm điểm t&iacute;ch lũy. C&aacute;c c&acirc;y cờ sẽ xuất hiện ngẫu nhi&ecirc;n trong bản đồ thi đấu.</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_liendaumonphai_07.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Sau khi kết th&uacute;c thời gian thi đấu, đồng đạo c&oacute; thể đến NPC Chưởng M&ocirc;n để nhận thưởng, chọn d&ograve;ng đối thoại&nbsp;<strong>Hoạt động thi đấu m&ocirc;n ph&aacute;i</strong>&nbsp;sau đ&oacute; chọn d&ograve;ng&nbsp;<strong>T&iacute;ch lũy đổi phần thưởng</strong>.</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_ldmonphai_08.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Những người chơi c&oacute; danh vọng thi đấu m&ocirc;n ph&aacute;i cao, đạt y&ecirc;u cầu, c&oacute; thể mua&nbsp;<strong>Trang bị Y Phục</strong>&nbsp;tại NPC Chưởng M&ocirc;n.</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_ldmonphai_09.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Ngo&agrave;i ra, v&agrave;o ng&agrave;y 01 mỗi th&aacute;ng, c&aacute;c&nbsp;<strong>T&acirc;n Nh&acirc;n Vương</strong>&nbsp;trong th&aacute;ng sẽ được đề cử bầu l&agrave;m Đại Sư Huynh / Đại Sư Tỷ.</h2>\r\n\r\n<h2>Những người trong m&ocirc;n ph&aacute;i c&oacute; thể bầu chọn&nbsp;<strong>Đại Sư Huynh / Đại Sư Tỷ</strong>&nbsp;th&ocirc;ng qua vật phẩm&nbsp;<strong>Tu Luyện Ch&acirc;u</strong>.</h2>\r\n\r\n<h2>Sử dụng Tu Luyện Ch&acirc;u, chọn d&ograve;ng&nbsp;<strong>Bỏ phiếu tuyển chọn Đại Sư Huynh/Đại Sư Tỷ</strong>, sẽ hiện ra danh s&aacute;ch những người ứng cử. Người đạt được số phiếu bầu cao nhất, sẽ nhận được danh hiệu Đại Sư Huynh hoặc Đại Sư Tỷ (đến Chưởng m&ocirc;n để nhận danh hiệu) v&agrave; 200 uy danh.</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_ldmonphai_10.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_ldmonphai_11.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_ldmonphai_12.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://img.zing.vn/wjx/images/data/news/1004/100409_ldmonphai_13.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n', '2023-03-14 21:48:20', NULL, ''),
(69, 'Vũ khí Đặc Chế (Thọ thương, Suy yếu, ...)', 'vu-khi-dac-che-tho-thuong-suy-yeu', NULL, '<h1>Danh vọng Cao Thủ Kim/Mộc/Thủy/Hỏa/Thổ</h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://i.imgur.com/zovpXvW.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://i.imgur.com/hUwPmx6.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<h3>Ấn&nbsp;<strong>F1</strong>&nbsp;chọn&nbsp;<strong>Danh vọng</strong>&nbsp;để xem cấp danh vọng&nbsp;<strong>Cao thủ</strong>&nbsp;hiện tại, c&oacute; 5 ngũ h&agrave;nh Kim - Mộc - Thủy - Hỏa v&agrave; Thổ</h3>\r\n\r\n<h3>Sử dụng lệnh b&agrave;i&nbsp;<strong>V&otilde; L&acirc;m Cao Thủ Lệnh</strong>&nbsp;hệ n&agrave;o th&igrave; sẽ gia tăng danh vọng cao thủ của hệ đấy, mỗi hệ đều c&oacute; 3 loại lệnh b&agrave;i, chi tiết:</h3>\r\n\r\n<h4><strong>Sơ:</strong>&nbsp;Tăng 10 danh vọng, rơi từ BOSS Ho&agrave;ng Kim 55, 75, 95</h4>\r\n\r\n<h4><strong>Trung:</strong>&nbsp;Tăng 20 danh vọng, rơi từ BOSS Ho&agrave;ng Kim 75, 95</h4>\r\n\r\n<h4><strong>Cao:</strong>&nbsp;Tăng 50 danh vọng, rơi từ BOSS Ho&agrave;ng Kim 95</h4>\r\n\r\n<h3>C&aacute;c cấp danh vọng cao thủ</h3>\r\n\r\n<h4><strong>Cấp 1 l&ecirc;n 2:</strong>&nbsp;50 điểm</h4>\r\n\r\n<h4><strong>Cấp 2 l&ecirc;n 3:</strong>&nbsp;150 điểm</h4>\r\n\r\n<h4><strong>Cấp 3 l&ecirc;n 4:</strong>&nbsp;400 điểm</h4>\r\n\r\n<h1>Mua Vũ kh&iacute; Đặc Chế 90/100</h1>\r\n\r\n<h2>Đến t&igrave;m gặp&nbsp;<strong>NPC Hoa Minh - B&aacute;n Vũ Kh&iacute; Đặc Chế</strong>&nbsp;tại&nbsp;<strong>183/252 L&acirc;m An Phủ</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://i.imgur.com/ZTfZdzx.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://i.imgur.com/or2K740.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Đến t&igrave;m gặp&nbsp;<strong>NPC Hoa Minh - B&aacute;n Vũ Kh&iacute; Đặc Chế</strong>&nbsp;tại&nbsp;<strong>183/252 L&acirc;m An Phủ</strong></h2>\r\n\r\n<h2>Ở đ&acirc;y c&oacute; b&aacute;n vũ kh&iacute; 90 v&agrave; 100 của 5 hệ, để mua được vũ kh&iacute; đặc chế y&ecirc;u cầu cấp danh vọng cao thủ, chi tiết</h2>\r\n\r\n<h3><strong>Cấp 2:</strong>&nbsp;Vũ kh&iacute; 90 loại 20%</h3>\r\n\r\n<h3><strong>Cấp 3:</strong>&nbsp;Vũ kh&iacute; 90 loại 4%</h3>\r\n\r\n<h3><strong>Cấp 4:</strong>&nbsp;Vũ kh&iacute; 100 loại 4%</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2023-03-14 21:48:26', NULL, ''),
(62, 'Danh sách Hoạt Động', 'danh-sach-hoat-dong', NULL, '<h1 style=\"text-align:left\"><span style=\"color:#993300\">Danh s&aacute;ch Hoạt Động</span></h1>\r\n\r\n<div class=\"ContentH4\" style=\"padding:0px; text-align:left\">\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\" style=\"text-align:center\">Hoạt Động</th>\r\n			<th scope=\"col\" style=\"text-align:center\">Thời Gian</th>\r\n			<th scope=\"col\" style=\"text-align:center\">Giới Hạn</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Khi&ecirc;u Chiến Mật Thất Du Long</strong></td>\r\n			<td style=\"text-align:center\">\r\n			<h4 style=\"text-align:left\">Cả Ng&agrave;y</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u Cầu : <strong>Đẳng Cấp 50 Trở L&ecirc;n</strong></h4>\r\n\r\n			<h4>Mỗi Ng&agrave;y Khi&ecirc;u Chiến <strong>50 Lần</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Tống Kim</strong></td>\r\n			<td>\r\n			<h4>Thời Gian B&aacute;o Danh :</h4>\r\n\r\n			<h5>10h50</h5>\r\n\r\n			<h5>16h50</h5>\r\n\r\n			<h5>20h50</h5>\r\n\r\n			<h5>22h50</h5>\r\n\r\n			<p style=\"text-align:center\"><span style=\"color:#c0392b\">Thời Gian B&aacute;o Danh L&agrave; 10 Ph&uacute;t</span></p>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u Cầu : <strong>Đẳng Cấp 60 Trở L&ecirc;n</strong></h4>\r\n\r\n			<h4>Mỗi Ng&agrave;y Tham Gia <strong>4&nbsp;Lần</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Bạch Hổ Đường</strong></td>\r\n			<td>\r\n			<h4>Bắt Đầu Ghi Danh :</h4>\r\n\r\n			<h5>Ph&uacute;t Thứ 30 Của Mỗi&nbsp;Giờ</h5>\r\n\r\n			<h4>Kết Th&uacute;c Ghi Danh :</h4>\r\n\r\n			<h5>Ph&uacute;t Thứ 59 Của Mỗi&nbsp;Giờ</h5>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u Cầu : <strong>Đẳng Cấp 50 Trở L&ecirc;n</strong></h4>\r\n\r\n			<h4>Mỗi Ng&agrave;y Tham Gia <strong>1&nbsp;Lần</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Ti&ecirc;u Dao Cốc</strong></td>\r\n			<td>\r\n			<h4>Cứ 30 Ph&uacute;t C&oacute; 1 Trận</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u Cầu : <strong>Đẳng Cấp 80 Trở L&ecirc;n</strong></h4>\r\n\r\n			<h4>Mỗi Ng&agrave;y Tham Gia <strong>1&nbsp;Lần</strong></h4>\r\n\r\n			<h4>Cộng Dồn Tối Đa 2 Tuần (14 Lần)</h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Bao Vạn Đồng</strong></td>\r\n			<td>\r\n			<h4>Cả Ng&agrave;y</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u Cầu : <strong>Đẳng Cấp 20 Trở L&ecirc;n</strong></h4>\r\n\r\n			<h4>Mỗi Ng&agrave;y Ho&agrave;n Th&agrave;nh&nbsp;<strong>50 Nhiệm Vụ</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Truy N&atilde; (Hải Tặc)</strong></td>\r\n			<td>\r\n			<h4>Cả Ng&agrave;y</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u Cầu :</h4>\r\n\r\n			<h5><strong>Đẳng Cấp 50 Trở L&ecirc;n&nbsp;</strong></h5>\r\n\r\n			<h5>20 điểm Uy Danh Giang Hồ</h5>\r\n\r\n			<h4>Mỗi Ng&agrave;y Truy N&atilde; : <strong>6 Hải Tặc</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Thương Hội</strong></td>\r\n			<td>\r\n			<h4>Cả Ng&agrave;y</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u Cầu :</h4>\r\n\r\n			<h5><strong>Đẳng Cấp 60 Trở L&ecirc;n&nbsp;</strong></h5>\r\n\r\n			<h5>60 điểm Uy Danh Giang Hồ</h5>\r\n\r\n			<h4>Mỗi Tuần Ho&agrave;n Th&agrave;nh <strong>40 Bước Nhiệm Vụ</strong></h4>\r\n\r\n			<h4 style=\"text-align:justify\">Ho&agrave;n th&agrave;nh&nbsp;<strong><span style=\"color:red\">trước 23h00 Chủ Nhật</span></strong>&nbsp;<br />\r\n			mới c&oacute; thể nhận tiếp nhiệm vụ tuần tiếp theo</h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>V&otilde; L&acirc;m Cao Thủ 55</strong></td>\r\n			<td>\r\n			<h4>Xuất Hiện L&uacute;c :</h4>\r\n\r\n			<h5>02h05</h5>\r\n\r\n			<h5>09h35</h5>\r\n\r\n			<h5>15h35</h5>\r\n\r\n			<h5>19h35</h5>\r\n\r\n			<h5>22h35</h5>\r\n\r\n			<p style=\"text-align:center\">H&Agrave;NG NG&Agrave;Y</p>\r\n			</td>\r\n			<td>\r\n			<h4>M&aacute;y chủ mở&nbsp;<strong>được 3 ng&agrave;y</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>V&otilde; L&acirc;m Cao Thủ 75</strong></td>\r\n			<td>\r\n			<h4>Xuất Hiện L&uacute;c :</h4>\r\n\r\n			<h5>02h05</h5>\r\n\r\n			<h5>09h35</h5>\r\n\r\n			<h5>15h35</h5>\r\n\r\n			<h5>19h35</h5>\r\n\r\n			<h5>22h35</h5>\r\n\r\n			<p style=\"text-align:center\">H&Agrave;NG NG&Agrave;Y</p>\r\n			</td>\r\n			<td>\r\n			<h4>M&aacute;y chủ mở&nbsp;<strong>giới hạn cấp đến 79</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>V&otilde; L&acirc;m Cao Thủ 95</strong></td>\r\n			<td>\r\n			<h4>Xuất Hiện L&uacute;c :</h4>\r\n\r\n			<h5>15h35</h5>\r\n\r\n			<h5>19h35</h5>\r\n\r\n			<h5>22h35</h5>\r\n\r\n			<p style=\"text-align:center\">H&Agrave;NG NG&Agrave;Y</p>\r\n			</td>\r\n			<td>\r\n			<h4>M&aacute;y chủ mở&nbsp;<strong>giới hạn cấp đến 99</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Tần Lăng - Tần Thuỷ Ho&agrave;ng</strong></td>\r\n			<td>\r\n			<h4>Xuất Hiện L&uacute;c :</h4>\r\n\r\n			<h5>15h35</h5>\r\n\r\n			<h5>22h35</h5>\r\n\r\n			<p style=\"text-align:center\">H&Agrave;NG NG&Agrave;Y</p>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u Cầu :</h4>\r\n\r\n			<h5><strong>Đẳng Cấp 100 Trở L&ecirc;n&nbsp;</strong></h5>\r\n\r\n			<h5>Đ&atilde; Gia Nhập 1 Bang Hội Bất Kỳ</h5>\r\n\r\n			<h4>M&aacute;y chủ mở&nbsp;<strong>giới hạn cấp đến 119</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Đo&aacute;n Hoa Đăng</strong></td>\r\n			<td>\r\n			<h4>Cả Ng&agrave;y</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u cầu&nbsp;<strong>đẳng cấp 50 trở l&ecirc;n</strong></h4>\r\n\r\n			<h4>Mỗi ng&agrave;y trả lời tối đa&nbsp;<strong>30 c&acirc;u hỏi</strong></h4>\r\n\r\n			<h4>NPC<strong> LỄ QUAN</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Thi Đấu M&ocirc;n Ph&aacute;i</strong></td>\r\n			<td>\r\n			<h4>Từ 19:30 đến 20:00 thứ 3 v&agrave; 5 H&agrave;ng Tuần</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u cầu&nbsp;<strong>đẳng cấp 50 trở l&ecirc;n</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>C&ocirc;ng Th&agrave;nh Chiến</strong></td>\r\n			<td>\r\n			<h4>Từ 19:30 đến 21:00 thứ 6 H&agrave;ng Tuần</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u cầu&nbsp;<strong>đẳng cấp 80 trở l&ecirc;n</strong></h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>Tranh Đoạt L&atilde;nh Thổ</strong></td>\r\n			<td>\r\n			<h4>Từ 20:30 đến 21:30 thứ 7 &amp; Chủ Nhật H&agrave;ng Tuần</h4>\r\n			</td>\r\n			<td>\r\n			<h4>Y&ecirc;u cầu&nbsp;<strong>đẳng cấp 80 trở l&ecirc;n</strong></h4>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n', '2023-03-14 21:48:52', NULL, ''),
(64, 'Uy Danh Giang Hồ', 'uy-danh-giang-ho', NULL, '<h1>L&agrave;m mới Uy Danh mỗi tuần</h1>\r\n\r\n<h2>Mỗi tuần&nbsp;<strong>từ 00h00 Thứ 2</strong>&nbsp;tất cả người chơi sẽ được l&agrave;m mới (reset) lại số điểm uy danh giang hồ về&nbsp;<strong>0 điểm</strong>.</h2>\r\n\r\n<h2>Số lần tham gia&nbsp;<strong>Ti&ecirc;u Dao Cốc, Hải Tặc</strong>&nbsp;sẽ l&agrave;m mới (reset) lại v&agrave;o thứ 2 h&agrave;ng tuần, vậy n&ecirc;n trước đ&oacute; c&aacute;c bạn h&atilde;y đi hết</h2>\r\n\r\n<h1>T&aacute;c dụng của Uy Danh</h1>\r\n\r\n<h2>Để tham gia hoạt động&nbsp;<span style=\"color:#c0392b\">Săn Hải Tặc</span>&nbsp;cần&nbsp;<strong>20 điểm Uy Danh</strong></h2>\r\n\r\n<h2>Để tham gia hoạt động&nbsp;<span style=\"color:#c0392b\">Thương Hội</span>&nbsp;cần&nbsp;<strong>50 điểm Uy Danh</strong></h2>\r\n\r\n<h2>Để mua&nbsp;<span style=\"color:#c0392b\">Tinh Hoạt Lực giảm gi&aacute;</span></h2>\r\n\r\n<h1>L&agrave;m sao để c&oacute; Uy Danh ?</h1>\r\n\r\n<h2>Mỗi ng&agrave;y uy danh giang hồ sẽ tự động giảm&nbsp;<strong>2%</strong>, kh&ocirc;ng vượt qu&aacute;&nbsp;<strong>20 điểm/1 ng&agrave;y</strong>.</h2>\r\n\r\n<h2>Hệ thống sẽ cập nhật xếp hạng uy danh v&agrave;o l&uacute;c&nbsp;<strong>00h00 H&agrave;ng Ng&agrave;y</strong>, ri&ecirc;ng thứ 2 h&agrave;ng tuần sẽ l&agrave;m mới (reset) lại to&agrave;n bộ uy danh của người chơi về&nbsp;<strong>0 điểm</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellspacing=\"0\" class=\"MyTable\" style=\"background-color:#ffffff; border-collapse:collapse; border-spacing:0px; border:1px solid #000000; font-family:Arial; text-align:left; text-decoration-color:initial; text-decoration-style:initial; text-decoration-thickness:initial; width:860px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\" style=\"background-color:#2a2a2a; text-align:center\">Hoạt động</th>\r\n			<th scope=\"col\" style=\"background-color:#2a2a2a; text-align:center\">Phần thưởng/Ghi ch&uacute;</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Ải Gia Tộc</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Tăng&nbsp;<strong>0</strong>&nbsp;điểm</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Trứng Du Long</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Tăng&nbsp;<strong>2</strong>&nbsp;điểm, mỗi ng&agrave;y sử dụng&nbsp;<strong>1 quả</strong></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Thương Hội</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Ho&agrave;n th&agrave;nh 40 nhiệm vụ tăng&nbsp;<strong>0</strong>&nbsp;điểm</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Du Long Uy Danh Ph&ugrave;</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Khi&ecirc;u chiến Du Long&nbsp;<strong><span style=\"color:red\">KH&Ocirc;NG NHẬN ĐƯỢC</span></strong></li>\r\n				<li style=\"text-align:justify\">Tăng&nbsp;<strong>0</strong>&nbsp;điểm</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Truy n&atilde; Hải Tặc</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Gặp&nbsp;<strong>H&igrave;nh Bổ Đầu</strong>&nbsp;tiếp nhận, ho&agrave;n th&agrave;nh tối đa&nbsp;<strong>6 lần</strong>&nbsp;mỗi ng&agrave;y, mỗi lần sẽ nhận&nbsp;<strong>1</strong>&nbsp;điểm, tối đa&nbsp;<strong>1 tuần</strong>&nbsp;l&agrave;&nbsp;<strong>30</strong>&nbsp;điểm.</li>\r\n				<li style=\"text-align:justify\">Uy Danh chỉ nhận được nếu diệt đ&uacute;ng qu&aacute;i theo cấp, v&iacute; dụ cấp 50 nhận nhiệm vụ cấp 50, cấp 60 nhận nhiệm vụ cấp 60, ...</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Nhiệm vụ Nghĩa Qu&acirc;n</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Tăng&nbsp;<strong>0</strong>&nbsp;điểm</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Ph&oacute; bản T&agrave;ng Bảo Đồ</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Ti&ecirc;u diệt BOSS trong ph&oacute; bản tất cả th&agrave;nh vi&ecirc;n được&nbsp;<strong>2</strong>&nbsp;điểm.</li>\r\n				<li style=\"text-align:justify\">Mỗi tuần tối đa&nbsp;<strong>40</strong>&nbsp;điểm, từ cấp 80 trở l&ecirc;n kh&ocirc;ng nhận được nữa.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Thi&ecirc;n Quỳnh Cung</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Tăng&nbsp;<strong>0<strong>&nbsp;điểm.</strong></strong></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Bạch Hổ Đường</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Giết BOSS của 3 tầng sẽ nhận được&nbsp;<strong>0</strong>&nbsp;điểm, qua 1 tầng được&nbsp;<strong>1</strong>&nbsp;điểm.</li>\r\n				<li style=\"text-align:justify\">Mỗi tuần tối đa&nbsp;<strong>60</strong>&nbsp;điểm</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Chiến trường Tống Kim</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\"><strong>Hạng 1</strong>&nbsp;nhận&nbsp;<strong>20</strong>&nbsp;điểm</li>\r\n				<li style=\"text-align:justify\"><strong>Hạng 2 đến 10</strong>&nbsp;nhận&nbsp;<strong>16</strong>&nbsp;điểm</li>\r\n				<li style=\"text-align:justify\"><strong>Hạng 11 đến 20</strong>&nbsp;nhận&nbsp;<strong>12</strong>&nbsp;điểm</li>\r\n				<li style=\"text-align:justify\">L&agrave; người chơi&nbsp;<strong>Đ&atilde; Gia Nhập Bang Hội</strong>&nbsp;tham gia Tống Kim đạt&nbsp;<strong>T&iacute;ch Lũy</strong>\r\n				<ul>\r\n					<li style=\"text-align:justify\">Tr&ecirc;n&nbsp;<strong>4500 điểm</strong>&nbsp;nhận&nbsp;<strong>10</strong>&nbsp;điểm</li>\r\n					<li style=\"text-align:justify\">Tr&ecirc;n&nbsp;<strong>3000 điểm</strong>&nbsp;nhận&nbsp;<strong>8</strong>&nbsp;điểm</li>\r\n				</ul>\r\n				</li>\r\n				<li style=\"text-align:justify\">Mỗi tuần tối đa&nbsp;<strong>180 điểm</strong>.</li>\r\n				<li style=\"text-align:justify\"><strong><span style=\"color:red\">(*) Lưu &yacute;:</span></strong>&nbsp;L&agrave; người chơi tham gia hết trận mới được nhận thưởng&nbsp;<strong>Uy Danh</strong></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Thi Đấu M&ocirc;n Ph&aacute;i</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\"><strong>Hạng 1</strong>&nbsp;nhận&nbsp;<strong>30</strong>&nbsp;điểm,&nbsp;<strong>Hạng 2 đến 4</strong>&nbsp;nhận&nbsp;<strong>20</strong>&nbsp;điểm,&nbsp;<strong>Hạng 5 đến 8</strong>&nbsp;nhận&nbsp;<strong>10</strong>&nbsp;điểm,&nbsp;<strong>Hạng 9 đến 16</strong>&nbsp;nhận&nbsp;<strong>6</strong>&nbsp;điểm.\r\n				<ul>\r\n					<li style=\"text-align:justify\"><strong>Hạng 1</strong>&nbsp;l&agrave; người chơi vượt qua v&ograve;ng loại (6 điểm), v&ograve;ng 1-16 (6 điểm), v&ograve;ng 1-8 (10 điểm), v&ograve;ng 1-4 (0 điểm), v&ograve;ng chung kết (10 điểm)</li>\r\n					<li style=\"text-align:justify\"><strong>Hạng 2 - 4</strong>&nbsp;l&agrave; người chơi vượt qua v&ograve;ng loại (6 điểm), v&ograve;ng 1-16 (6 điểm), v&ograve;ng 1-8 (10 điểm), v&ograve;ng 1-4 (0 điểm)</li>\r\n					<li style=\"text-align:justify\"><strong>Hạng 5 - 8</strong>&nbsp;l&agrave; người chơi vượt qua v&ograve;ng loại (6 điểm), v&ograve;ng 1-16 (6 điểm)</li>\r\n					<li style=\"text-align:justify\"><strong>Hạng 9 - 16</strong>&nbsp;l&agrave; người chơi vượt qua v&ograve;ng loại (6 điểm)\r\n					<ul>\r\n						<li style=\"text-align:justify\">T&iacute;ch lũy cờ&nbsp;<strong>4800 điểm</strong>&nbsp;nhận&nbsp;<strong>8</strong>&nbsp;điểm,&nbsp;<strong>4000 điểm</strong>&nbsp;nhận&nbsp;<strong>6</strong>&nbsp;điểm,&nbsp;<strong>3200 điểm</strong>&nbsp;nhận&nbsp;<strong>5</strong>&nbsp;điểm,&nbsp;<strong>tr&ecirc;n 2400 điểm</strong>&nbsp;nhận&nbsp;<strong>4</strong>&nbsp;điểm.</li>\r\n						<li style=\"text-align:justify\">Mỗi tuần tối đa&nbsp;<strong>60</strong>&nbsp;điểm.</li>\r\n					</ul>\r\n					</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">T&acirc;n Nh&acirc;n Vương</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Nhận&nbsp;<strong>100</strong>&nbsp;điểm.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Đại sư huynh/Sư tỷ m&ocirc;n ph&aacute;i</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">L&agrave; Đại Sư Huynh / Sư Tỷ tăng nhận&nbsp;<strong>0</strong>&nbsp;điểm.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Đo&aacute;n Hoa Đăng</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Trả lời đủ&nbsp;<strong>30 c&acirc;u</strong>&nbsp;với mỗi c&acirc;u trả lời đ&uacute;ng nhận&nbsp;<strong>1</strong>&nbsp;điểm.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Ti&ecirc;u Dao Cốc</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Qua ải 2 được&nbsp;<strong>2</strong>&nbsp;điểm</li>\r\n				<li style=\"text-align:justify\">Qua ải 3 được&nbsp;<strong>4</strong>&nbsp;điểm</li>\r\n				<li style=\"text-align:justify\">Qua ải 4 được&nbsp;<strong>10</strong>&nbsp;điểm</li>\r\n				<li style=\"text-align:justify\">Qua ải 5 được&nbsp;<strong>16</strong>&nbsp;điểm</li>\r\n				<li style=\"text-align:justify\">Mỗi tuần tối đa&nbsp;<strong>100</strong>&nbsp;điểm.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Khi&ecirc;u chiến V&otilde; L&acirc;m Cao Thủ 55/75/95</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Mỗi khi ti&ecirc;u diệt được 1 cao thủ cấp 55, 75, 95 tất cả th&agrave;nh vi&ecirc;n trong đội đều nhận được&nbsp;<strong>2, 3, 5</strong>&nbsp;điểm.</li>\r\n				<li style=\"text-align:justify\">Mỗi tuần tối đa&nbsp;<strong>60</strong>&nbsp;điểm, ti&ecirc;u diệt boss từ&nbsp;<strong>C&acirc;u Hồn Ngọc</strong>&nbsp;kh&ocirc;ng được uy danh.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Tranh Đoạt L&atilde;nh Thổ</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Bang hội c&oacute;&nbsp;<strong>1 L&atilde;nh Thổ</strong>&nbsp;th&agrave;nh vi&ecirc;n&nbsp;<strong>đủ 800 điểm C&ocirc;ng Trạng</strong>&nbsp;nhận&nbsp;<strong>30</strong>&nbsp;điểm</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; border-color:#000000; border-style:solid; border-width:1px; text-align:center\">Qu&acirc;n Doanh</td>\r\n			<td style=\"background-color:#ffffff; border-bottom:1px solid #000000; border-left:1px solid #000000; border-right:1px solid #000000; border-top:1px solid #000000; text-align:left\">\r\n			<ul style=\"margin-left:20px\">\r\n				<li style=\"text-align:justify\">Ho&agrave;n th&agrave;nh nhiệm vụ ch&iacute;nh tuyến nhận&nbsp;<strong>6</strong>&nbsp;điểm</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2023-03-14 21:48:48', NULL, ''),
(65, 'Truy Nã Hải Tặc', 'truy-na-hai-tac', NULL, '<p>H&agrave;nh hiệp trượng nghĩa, ti&ecirc;u diệt &aacute;c b&aacute;&hellip; những nhiệm vụ đầy ch&ocirc;ng gai đang chờ đợi c&aacute;c vị hảo h&aacute;n tr&ecirc;n bước đường b&ocirc;n tẩu giang hồ. C&ograve;n chờ g&igrave; nữa? Gươm đ&atilde; s&aacute;ng, trống đ&atilde; giục&hellip; tất cả đ&atilde; sẵn s&agrave;ng để hối th&uacute;c c&aacute;c tr&aacute;ng sĩ l&ecirc;n đường thực hiện nhiệm vụ Quan Phủ c&ugrave;ng Kiếm Thế.</p>\r\n\r\n<h1>Thực hiện nhiệm vụ</h1>\r\n\r\n<h2>Điều kiện tham dự</h2>\r\n\r\n<h3><strong>Nh&acirc;n vật đạt cấp 50</strong>,&nbsp;<strong>đ&atilde; ho&agrave;n th&agrave;nh nhiệm vụ ch&iacute;nh tuyến 50</strong>&nbsp;v&agrave;&nbsp;<strong>điểm uy danh giang hồ đạt 20</strong>&nbsp;c&oacute; thể đến H&igrave;nh Bộ Bổ Đầu c&aacute;c th&agrave;nh thị lớn để nhận nhiệm vụ.</h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Nhiệm vụ quan phủ Kiếm Thế\" src=\"http://img.zing.vn/wjx/images/bai-viet/nhiem-vu/quanphu01.jpg\" style=\"height:338px; width:450px\" /></p>\r\n\r\n<h3>Lưu &yacute;</h3>\r\n\r\n<h4>Trong một ng&agrave;y mỗi nh&acirc;n vật chỉ được tham dự tối đa 6 lần nhiệm vụ Quan Phủ.</h4>\r\n\r\n<h4>Nh&acirc;n vật đạt cấp 50 nhưng chưa ho&agrave;n th&agrave;nh nhiệm vụ ch&iacute;nh tuyến 50 kh&ocirc;ng thể tham dự.</h4>\r\n\r\n<h4>Nh&acirc;n vật đạt cấp 50 nhưng chưa được 20 điểm Uy Danh Giang hồ kh&ocirc;ng thể tham dự.</h4>\r\n\r\n<h4>Nh&acirc;n vật nhỏ hơn cấp 50 v&agrave; đ&atilde; gia nhập m&ocirc;n ph&aacute;i th&igrave; kh&ocirc;ng thể tham gia.</h4>\r\n\r\n<h1>Tiếp nhận nhiệm vụ</h1>\r\n\r\n<h3>Khi tiếp nhận nhiệm vụ, đồng đạo c&oacute; thể căn cứ theo cấp độ của bản th&acirc;n m&agrave; lựa chọn truy bắt những cường đạo cấp độ kh&aacute;c nhau.</h3>\r\n\r\n<h3>C&oacute; thể mở ra giao diện nhiệm vụ phần F4 để biết th&ecirc;m th&ocirc;ng tin. Căn cứ v&agrave;o tọa độ địa điểm m&agrave; nhiệm vụ gợi &yacute; sẽ t&igrave;m được cường đạo cần truy n&atilde;.</h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Nhiệm vụ quan phủ Kiếm Thế\" src=\"http://img.zing.vn/wjx/images/bai-viet/nhiem-vu/quanphu06.jpg\" style=\"height:338px; width:450px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<img alt=\"Nhiệm vụ quan phủ Kiếm Thế\" src=\"http://img.zing.vn/wjx/images/bai-viet/nhiem-vu/quanphu08.jpg\" style=\"height:338px; width:450px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<img alt=\"Nhiệm vụ quan phủ Kiếm Thế\" src=\"http://img.zing.vn/wjx/images/bai-viet/nhiem-vu/quanphu09.jpg\" style=\"height:338px; width:450px\" /></p>\r\n\r\n<h3>Sau khi giết được cường đạo cần truy n&atilde; sẽ c&oacute; vật phẩm v&agrave; Huyền Tinh rớt ra.</h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Nhiệm vụ quan phủ Kiếm Thế\" src=\"http://img.zing.vn/wjx/images/bai-viet/nhiem-vu/quanphu10.jpg\" style=\"height:338px; width:450px\" /></p>\r\n\r\n<h3>Ti&ecirc;u diệt được hắn l&agrave; c&oacute; thể quay về H&igrave;nh Bộ Bổ Đầu trao trả nhiệm vụ.</h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Nhiệm vụ quan phủ Kiếm Thế\" src=\"http://img.zing.vn/wjx/images/bai-viet/nhiem-vu/quanphu11.jpg\" style=\"height:338px; width:450px\" /></p>\r\n\r\n<h3>Lưu &yacute;</h3>\r\n\r\n<h4>Số lần nhận nhiệm vụ ng&agrave;y h&ocirc;m trước chưa nhận c&oacute; thể cộng dồn lại v&agrave;o ng&agrave;y h&ocirc;m sau.</h4>\r\n\r\n<h4>Nhiều nhất c&oacute; thể cộng dồn l&agrave; 36 lần.</h4>\r\n\r\n<h4>Phần thưởng nhiệm vụ t&ugrave;y theo cấp độ của Hải Tặc.</h4>\r\n\r\n<h4>Đồng đạo c&oacute; thể tổ đội c&ugrave;ng nhau nhận nhiệm vụ truy n&atilde;. Chỉ cần mọi th&agrave;nh vi&ecirc;n trong đội đều đạt y&ecirc;u cầu nhận nhiệm vụ l&agrave; c&oacute; thể c&ugrave;ng nhau tiếp nhận v&agrave; truy bắt cường đạo c&ugrave;ng l&atilde;nh thưởng.</h4>\r\n\r\n<h1>Phần thưởng</h1>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<thead>\r\n		<tr>\r\n			<th>Nhiệm vụ</th>\r\n			<th>Phần thưởng</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:middle\">Ho&agrave;n th&agrave;nh nhiệm vụ cấp 50</td>\r\n			<td style=\"vertical-align:middle\"><strong>1 Danh Bổ Lệnh, 1 uy danh giang hồ</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:middle\">Ho&agrave;n th&agrave;nh nhiệm vụ cấp 60</td>\r\n			<td style=\"vertical-align:middle\"><strong>2 Danh Bổ Lệnh, 1 uy danh giang hồ</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:middle\">Ho&agrave;n th&agrave;nh nhiệm vụ cấp 70</td>\r\n			<td style=\"vertical-align:middle\"><strong>3 Danh Bổ Lệnh, 1 uy danh giang hồ</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:middle\">Ho&agrave;n th&agrave;nh nhiệm vụ cấp 80</td>\r\n			<td style=\"vertical-align:middle\"><strong>4 Danh Bổ Lệnh, 1 uy danh giang hồ</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:middle\">Ho&agrave;n th&agrave;nh nhiệm vụ cấp 90</td>\r\n			<td style=\"vertical-align:middle\"><strong>5 Danh Bổ Lệnh, 1 uy danh giang hồ</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2>Những vấn đề cần ch&uacute; &yacute;</h2>\r\n\r\n<h3>Mỗi tuần nhận tối đa 30 điểm uy danh.</h3>\r\n\r\n<h3>Mỗi ng&agrave;y nhận được 6 lần nhiệm vụ, t&iacute;ch lũy tối đa 36 lần.</h3>\r\n\r\n<h3>Mỗi lần huỷ sẽ ti&ecirc;u hao 1 lần nhiệm vụ.</h3>\r\n\r\n<h3>C&oacute; thể d&ugrave;ng Danh Bổ Lệnh để đổi V&otilde; L&acirc;m Mật Tịch v&agrave; Tẩy Tuỷ Kinh ở NPC Bổ Đầu H&igrave;nh Bộ.</h3>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<thead>\r\n		<tr>\r\n			<th>Điều kiện đổi phần thưởng</th>\r\n			<th>Phần thưởng</th>\r\n			<th>Ghi ch&uacute;</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:middle\">300 Danh Bổ Lệnh</td>\r\n			<td style=\"vertical-align:middle\"><img alt=\"Võ lâm mật tịch\" src=\"http://img.zing.vn/wjx/images/bai-viet/nhiem-vu/quanphu15.jpg\" style=\"height:40px; width:40px\" /><br />\r\n			<strong>V&otilde; l&acirc;m mật tịch</strong>&nbsp;(sơ)</td>\r\n			<td style=\"vertical-align:middle\">C&oacute; thể học tối đa 5 quyển V&otilde; L&acirc;m Mật Tịch (sơ) v&agrave; Tẩy Tuỷ Kinh (sơ).<br />\r\n			Học V&otilde; L&acirc;m Mật Tịch (sơ) được 1 điểm Kỹ năng.</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:middle\">300 Danh Bổ Lệnh</td>\r\n			<td style=\"vertical-align:middle\"><img alt=\"Võ lâm mật tịch\" src=\"http://img.zing.vn/wjx/images/bai-viet/nhiem-vu/quanphu16.jpg\" style=\"height:40px; width:40px\" /><br />\r\n			<strong>Tẩy tủy kinh&nbsp;</strong>(sơ)</td>\r\n			<td style=\"vertical-align:middle\">Học Tẩy Tuỷ Kinh (sơ) được 10 điểm tiềm năng.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2023-03-14 21:48:44', NULL, '');
INSERT INTO `blog_posts_seo` (`postID`, `postTitle`, `postSlug`, `postDesc`, `postCont`, `postDate`, `postTags`, `postImage`) VALUES
(66, 'Nhiệm vụ Thương Hội', 'nhiem-vu-thuong-hoi', NULL, '<p>Nhiệm vụ Thương Hội l&agrave; một trong những nhiệm vụ mang đến nhiều phần thưởng cho qu&yacute; đồng đạo. Đồng đạo dễ d&agrave;ng tham gia nhiệm vụ n&agrave;y v&igrave; điều kiện kh&aacute; dễ d&agrave;ng.</p>\r\n\r\n<h1 style=\"text-align:left\">Điều kiện tham gia</h1>\r\n\r\n<div class=\"ContentH4\" style=\"padding:0px; text-align:left\">\r\n<h3>Nhiệm vụ thương hội sang tuần mới t&iacute;nh từ&nbsp;<strong>23h00 Ng&agrave;y Chủ Nhật H&agrave;ng Tuần</strong>&nbsp;đồng nghĩa với việc c&aacute;c bạn muốn nhận nhiệm vụ tuần sau th&igrave; phải ho&agrave;n th&agrave;nh trước&nbsp;<strong>23h00 Chủ Nhật</strong></h3>\r\n\r\n<h3>Trường hợp nhận nhiệm vụ tuần trước sang tuần mới l&agrave;m th&igrave; sẽ t&iacute;nh bạn ho&agrave;n th&agrave;nh của sang tuần, kh&ocirc;ng thể nhận th&ecirc;m</h3>\r\n\r\n<h3>Từ&nbsp;<strong>23h00 đến 24h00 Chủ Nhật</strong>&nbsp;sẽ kh&ocirc;ng thể đối thoại với&nbsp;<strong>NPC Chủ Thương Hội</strong></h3>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\"><img src=\"http://img.zing.vn/wjx/images/data/mainsite/thuonghoi_16.jpg\" style=\"border:0px\" /></span></span></p>\r\n</div>\r\n\r\n<h1 style=\"text-align:left\">C&aacute;ch thức tham gia</h1>\r\n\r\n<div class=\"ContentH4\" style=\"padding:0px; text-align:left\">\r\n<h3>Khi hội đủ điều kiện, đồng đạo c&oacute; thể t&igrave;m đến Chủ Thương Hội để nhận nhiệm vụ Thương Hội. Một nhiệm vụ Thương Hội bao gồm 40 bước.</h3>\r\n\r\n<h3>Sau khi ho&agrave;n th&agrave;nh xong 40 bước mới được xem l&agrave; ho&agrave;n th&agrave;nh 1 nhiệm vụ Thương Hội.</h3>\r\n\r\n<table align=\"center\" border=\"1\" cellspacing=\"0\" class=\"MyTable\" style=\"border-collapse:collapse; border-spacing:0px; border:1px solid #000000; text-align:left; width:860px\">\r\n	<thead>\r\n		<tr>\r\n			<th style=\"background-color:#2a2a2a; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">NPC Chủ Thương Hội</span></span></th>\r\n			<th style=\"background-color:#2a2a2a; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">Vị tr&iacute;</span></span></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"7\" style=\"border-color:#000000; border-style:solid; border-width:1px; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\"><img alt=\"Kiếm Thế\" class=\"BorderImg\" src=\"http://img.zing.vn/wjx/images/data/mainsite/thuonghoi_03.jpg\" style=\"border:0px; height:218px; width:196px\" /></span></span></td>\r\n			<td style=\"border-color:#000000; border-style:solid; border-width:1px; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">Biện Kinh Phủ - 200/187</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:#000000; border-style:solid; border-width:1px; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">Dương Ch&acirc;u Phủ - 197/207</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:#000000; border-style:solid; border-width:1px; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">L&acirc;m An Phủ - 219/244</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:#000000; border-style:solid; border-width:1px; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">Phượng Tường Phủ - 219/212</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:#000000; border-style:solid; border-width:1px; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">Th&agrave;nh Đ&ocirc; Phủ - 201/203</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:#000000; border-style:solid; border-width:1px; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">Tương Dương Phủ - 200/191</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-color:#000000; border-style:solid; border-width:1px; text-align:center\"><span style=\"color:#555555\"><span style=\"background-color:#ffffff\">Đại L&yacute; Phủ - 185/212</span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<h1 style=\"text-align:left\"><strong>Nhiệm vụ thương hội bao gồm 3 loại ch&iacute;nh:</strong></h1>\r\n\r\n<div class=\"ContentH5\" style=\"padding:0px; text-align:left\">\r\n<h3><strong>Đưa thư</strong></h3>\r\n\r\n<h4><strong>Đưa thư Tống Kim:</strong>&nbsp;Tham gia hoạt động Tống Kim, chạy ra giữa bản đồ t&igrave;m NPC Đưa thư Thương Hội</h4>\r\n\r\n<h4><strong>Đưa thư Bạch Hổ Đường:</strong>&nbsp;Tham gia hoạt động Bạch Hổ Đường l&ecirc;n tầng 2 t&igrave;m NPC Đưa thư Thương Hội</h4>\r\n\r\n<h3><strong>Thu thập</strong></h3>\r\n\r\n<h4><strong>Lệnh b&agrave;i Bạch Hổ Đường 1, 2 hoặc 3:</strong>&nbsp;Tham gia hoạt động Bạch Hổ Đường, đ&aacute;nh qu&aacute;i sẽ c&oacute; x&aacute;c xuất nhận được - Y&ecirc;u cầu đang nhận nhiệm vụ thương hội</h4>\r\n\r\n<h4><strong>Lệnh b&agrave;i Ti&ecirc;u Dao Cốc 1, 2, 3, 4 hoặc 5:</strong>&nbsp;Tham gia hoạt động Ti&ecirc;u Dao Cốc, đ&aacute;nh qu&aacute;i sẽ c&oacute; x&aacute;c xuất nhận được - Y&ecirc;u cầu đang nhận nhiệm vụ thương hội</h4>\r\n\r\n<h4><strong>Lệnh b&agrave;i Thống Lĩnh, Ph&oacute; Tướng hoặc Đại Tướng Tống Kim:</strong>&nbsp;Tham gia hoạt động Tống Kim, ti&ecirc;u diệt Thống Lĩnh / Ph&oacute; Tướng / Đại Tướng sẽ nhận được - Y&ecirc;u cầu đang nhận nhiệm vụ thương hội</h4>\r\n\r\n<h3><strong>Sưu tầm</strong></h3>\r\n\r\n<h4><strong>Lệnh b&agrave;i Thi Đấu M&ocirc;n Ph&aacute;i (Sơ):</strong>&nbsp;Tham gia hoạt động Thi Đấu M&ocirc;n Ph&aacute;i, Đại Hội Tỷ V&otilde; mở Rương x&aacute;c xuất nhận được</h4>\r\n\r\n<h4><strong>Lệnh b&agrave;i Gia Tộc (Sơ):</strong>&nbsp;Tham gia hoạt động Vượt Ải Gia Tộc mở Rương khi kết th&uacute;c hoạt động x&aacute;c xuất nhận được</h4>\r\n\r\n<h4><strong>Lệnh b&agrave;i Bạch Hổ Đường (Sơ):</strong>&nbsp;Tham gia hoạt động Bạch Hổ Đường diệt BOSS Tầng 1 - 2 - 3 c&oacute; x&aacute;c xuất nhận được</h4>\r\n\r\n<h1>Phần Thưởng</h1>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<table align=\"center\" style=\"width:450px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\" style=\"text-align:center\"><strong>C&aacute;c bước</strong></th>\r\n			<th scope=\"col\" style=\"text-align:center\"><strong>Phần thưởng</strong></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\">10 bước</td>\r\n			<td style=\"text-align:center\">50 vạn bạc kh&oacute;a, 1 huyền tinh cấp 7</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">20 bước</td>\r\n			<td style=\"text-align:center\">100 vạn bạc kh&oacute;a, 2 huyền tinh cấp 7</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">30 bước</td>\r\n			<td style=\"text-align:center\">150 vạn bạc kh&oacute;a, 3 huyền tinh cấp 7</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">40 bước</td>\r\n			<td style=\"text-align:center\">200 vạn bạc kh&oacute;a, 2 huyền tinh cấp 8</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n', '2023-03-14 21:48:35', NULL, ''),
(67, 'Tinh Hoạt Lực & Phúc lợi Giảm Giá tới 80%', 'tinh-hoat-luc-phuc-loi-giam-gia-toi-80', NULL, '<h1>Ph&uacute;c lợi Tinh Hoạt Lực</h1>\r\n\r\n<h3 style=\"text-align:justify\">Đồng thời thỏa m&atilde;n 2 điều kiện&nbsp;<strong>Uy Danh Giang Hồ</strong>&nbsp;đạt&nbsp;<strong>60 điểm</strong>&nbsp;v&agrave;&nbsp;<strong>Xếp Hạng Uy Danh từ 1 - 500</strong>&nbsp;khi mua sẽ được giảm gi&aacute;&nbsp;<strong>80%</strong></h3>\r\n\r\n<h3 style=\"text-align:justify\">Đồng thời thỏa m&atilde;n 2 điều kiện&nbsp;<strong>Uy Danh Giang Hồ</strong>&nbsp;đạt&nbsp;<strong>60 điểm</strong>&nbsp;v&agrave;&nbsp;<strong>Xếp Hạng Uy Danh từ 501 - 700</strong>&nbsp;khi mua sẽ được giảm gi&aacute;&nbsp;<strong>40%</strong></h3>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Xếp hạng Uy Danh Giang Hồ</strong>&nbsp;sẽ được cập nhật v&agrave;o&nbsp;<strong>l&uacute;c 00h00 H&agrave;ng Ng&agrave;y</strong>, ri&ecirc;ng thứ 2 h&agrave;ng tuần c&oacute; th&ecirc;m 1 đợt l&uacute;c&nbsp;<strong>03h00</strong></h3>\r\n\r\n<h3 style=\"text-align:justify\">Điểm&nbsp;<strong>Uy Danh Giang Hồ</strong>&nbsp;-&nbsp;<a href=\"/uy-danh-giang-ho.html\" style=\"text-decoration:none; color:#993300\">&gt;&gt;&gt; XEM HƯỚNG DẪN UY DANH GIANG HỒ &lt;&lt;&lt;</a>&nbsp;sẽ được l&agrave;m mới (reset) về&nbsp;<strong>0 điểm</strong>&nbsp;v&agrave;o mỗi&nbsp;<strong>Thứ 2 H&agrave;ng Tuần</strong></h3>\r\n\r\n<h3 style=\"text-align:justify\">Để mua&nbsp;<strong>Tinh Hoạt Lực</strong>&nbsp;c&aacute;c bạn mở giao diện&nbsp;<img src=\"https://i.imgur.com/kgkUZms.jpg\" style=\"border:0px\" />&nbsp;<strong>H&agrave;nh Trang (Ph&iacute;m tắt F2)</strong>&nbsp;t&igrave;m vật phẩm&nbsp;<img src=\"https://i.imgur.com/zU1OCYf.jpg\" style=\"border:0px\" />&nbsp;<strong>T&uacute;i T&acirc;n Thủ</strong>&nbsp;mở ra để mua</h3>\r\n\r\n<table align=\"center\" border=\"0\" cellspacing=\"0\" style=\"width:100%\">\r\n	<thead>\r\n		<tr>\r\n			<th rowspan=\"2\">Vật phẩm</th>\r\n			<th colspan=\"3\">Đơn gi&aacute; 1 b&igrave;nh (Đồng thường)</th>\r\n			<th rowspan=\"2\">Mua mỗi ng&agrave;y</th>\r\n		</tr>\r\n		<tr>\r\n			<th>Gi&aacute; Gốc</th>\r\n			<th>Giảm 40%</th>\r\n			<th>Giảm 80%</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><img src=\"https://i.imgur.com/OYRsY3b.jpg\" /></p>\r\n\r\n			<p style=\"text-align:center\"><strong>Tinh Lực (Tiểu)</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">4000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">2400</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">800</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">5</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><img src=\"https://i.imgur.com/6G212Ot.jpg\" /></p>\r\n\r\n			<p style=\"text-align:center\"><strong>Hoạt Lực (Tiểu)</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">4000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">2400</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">800</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">5</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><img src=\"https://i.imgur.com/A0QgCQS.jpg\" /></p>\r\n\r\n			<p style=\"text-align:center\"><strong>Tinh Lực (Trung)</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">12000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">7200</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">2400</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">5</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><img src=\"https://i.imgur.com/89vrIV0.jpg\" /></p>\r\n\r\n			<p style=\"text-align:center\"><strong>Hoạt Lực (Trung)</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">12000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">7200</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">2400</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">5</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><img src=\"https://i.imgur.com/l52lYgs.jpg\" /></p>\r\n\r\n			<p style=\"text-align:center\"><strong>Tinh Lực (Đại)</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">30000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">18000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">6000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">10</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><img src=\"https://i.imgur.com/2YR5KdU.jpg\" /></p>\r\n\r\n			<p style=\"text-align:center\"><strong>Hoạt Lực (Đại)</strong></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">30000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">18000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">6000</p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align:center\">10</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2023-03-14 21:48:40', NULL, ''),
(75, 'Công Thành Đại Chiến', 'cong-thanh-dai-chien', NULL, '<p style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/congthanhdaichien.png\" style=\"height:308px; width:658px\" /></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong>H</strong></span>oạt động C&ocirc;ng Th&agrave;nh Đại Chiến l&agrave; 1 hoạt động PK Bang, chiếm giữ Long Trụ v&agrave; Ngai V&agrave;ng, để t&iacute;ch lũy điểm c&aacute; nh&acirc;n nhầm mang về cho m&igrave;nh v&ocirc; v&agrave;ng phần thưởng hấp dẫn.</p>\r\n\r\n<p><strong><span style=\"font-size:18px\">M</span></strong>ỗi PC được ph&eacute;p tham gia 6 nh&acirc;n vật , t&iacute;ch lũy c&agrave;ng nhiều nhận thưởng c&agrave;ng cao, ai sẽ nắm giữ Ngai V&agrave;ng trong kỳ&nbsp;<span style=\"color:#c0392b\"><strong>C&ocirc;ng Th&agrave;nh Đại Chiến</strong></span> n&agrave;y?</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/ctcmap.png\" style=\"height:149px; width:239px\" /></p>\r\n\r\n<h1>Th&ocirc;ng Tin &amp; NPC Li&ecirc;n Quan.</h1>\r\n\r\n<h2><strong>Thời Gian :</strong></h2>\r\n\r\n<h3>Ng&agrave;y Thi Đấu Trong Tuần : Thứ Tư (H&agrave;ng Tuần)</h3>\r\n\r\n<h3>Thời Gian B&aacute;o Danh :&nbsp;từ <strong>19h30</strong>&nbsp;đến <strong>19h59</strong>.</h3>\r\n\r\n<h3>Thời Gian Tập Kết V&agrave;o Bản Đồ&nbsp;: <strong>19h45</strong> (C&oacute; Thể V&agrave;o Bản Đồ)</h3>\r\n\r\n<h3>Thời Gian Khai Chiến :&nbsp;từ <strong>20h00</strong> đến <strong>20h50</strong></h3>\r\n\r\n<h3>Thời Gian Nhận Thưởng : Bắt Đầu Từ <strong>20h55</strong> đến <strong>23h55</strong> C&ugrave;ng Ng&agrave;y.</h3>\r\n\r\n<h2><strong>Quy Tắc &amp; Giới Hạn.</strong></h2>\r\n\r\n<h3>Mỗi PC được ph&eacute;p tham gia <span style=\"color:#c0392b\"><strong>6</strong></span> nh&acirc;n vật</h3>\r\n\r\n<h3>Bản đồ cho ph&eacute;p tổ đội</h3>\r\n\r\n<h3>Khi bị hạ gục sẽ về điểm tập trung, kh&ocirc;ng thể hồi sinh</h3>\r\n\r\n<h3>Bản đồ C&ocirc;ng th&agrave;nh chiến sẽ kh&oacute;a trạng th&aacute;i PK Bang hội&nbsp;<strong>(<span style=\"color:#c0392b\">KH&Ocirc;NG THỂ THAY ĐỔI PK</span>)</strong></h3>\r\n\r\n<h2><strong>NPC Li&ecirc;n Quan.</strong></h2>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:450px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Tướng Viễn Chinh</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/ctc2.png\" style=\"height:207px; width:184px\" /><br />\r\n			<strong>Vị Tr&iacute; </strong>: 217/218 Phượng Tường</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h1>Th&ocirc;ng Tin Hoạt Động.</h1>\r\n\r\n<h2><strong>B&aacute;o Danh :</strong></h2>\r\n\r\n<h3><strong>Thời gian b&aacute;o danh:</strong>&nbsp;Từ 19h30&nbsp;đến 19h59 Thứ 4 H&agrave;ng tuần th&ocirc;ng qua&nbsp;<strong>NPC Tướng Viễn Chinh</strong>&nbsp;tại Phượng Tường Phủ</h3>\r\n\r\n<h3><strong>Thủ lĩnh Bang hội:</strong>&nbsp;Phải đến đối thoại c&ugrave;ng <strong>NPC Tướng Viễn Chinh</strong> để&nbsp;<strong>b&aacute;o danh cho Bang hội</strong></h3>\r\n\r\n<h3><strong>Điều kiện Bang hội:</strong>&nbsp;Tối đa cho ph&eacute;p&nbsp;<strong>4</strong>&nbsp;Bang Hội được ph&eacute;p tham gia (Xem th&ocirc;ng b&aacute;o t&ecirc;n Bang Hội tr&ecirc;n fanpage)</h3>\r\n\r\n<h3><strong>Điều kiện người chơi:</strong>&nbsp;L&agrave; th&agrave;nh vi&ecirc;n của Bang hội đ&atilde; được Bang chủ b&aacute;o danh hoạt động th&agrave;nh c&ocirc;ng</h3>\r\n\r\n<h1>Khai Chiến :</h1>\r\n\r\n<h3>Khi tham gia v&agrave;o C&ocirc;ng Th&agrave;nh Chiến c&aacute;c Bang hội sẽ được ngẫu nhi&ecirc;n về 4 vị tr&iacute; tập trung kh&aacute;c nhau</h3>\r\n\r\n<h3>V&agrave;o l&uacute;c&nbsp;<strong>20h00</strong>&nbsp;, 4 Long Trụ sẽ xuất hiện ở 4 g&oacute;c, v&agrave; Long Mạch ở trung t&acirc;m của bản đổ C&ocirc;ng Th&agrave;nh Chiến</h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/2map.jpg\" style=\"height:465px; width:750px\" /></p>\r\n\r\n<h2><strong>Quy Tắc T&iacute;nh Điểm:</strong></h2>\r\n\r\n<h3>Điểm t&iacute;ch lũy mỗi Long Trụ: <strong>30 điểm</strong></h3>\r\n\r\n<h3>Điểm t&iacute;ch lũy Ngai V&agrave;ng: <strong>70 điểm</strong></h3>\r\n\r\n<h3>Bảo Vệ V&agrave; Chiếm Giữ Long Trụ Đối Với Mỗi Th&agrave;nh Vi&ecirc;n Ở Gần (<strong>+5 Điểm</strong>)</h3>\r\n\r\n<h2><strong>Phần Thưởng :</strong></h2>\r\n\r\n<p><strong>T&iacute;ch Luỹ Bảo Vệ Trụ Sau Mỗi 10 Gi&acirc;y :</strong></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:400px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Phần Thưởng</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\">50.000 Điểm Kinh Nghiệm</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">2 Điểm T&iacute;ch Luỹ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:400px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Lưu &Yacute; :</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>Phần Thưởng Bảo Vệ Trụ Của Long Trụ V&agrave; Ngai V&agrave;ng Đều Bằng Nhau. Kh&ocirc;ng c&oacute; sự ch&ecirc;nh lệch</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><strong>Phần Thưởng Kết Th&uacute;c:</strong></h2>\r\n\r\n<h3><strong>Phần Thưởng Dựa Tr&ecirc;n Điểm T&iacute;ch Luỹ Nhiều Nhất Của Mỗi Bang Sau Khi Kết Th&uacute;c Trận Chiến.</strong></h3>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:700px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Hạng Bang Hội</th>\r\n			<th scope=\"col\">Chức Vụ</th>\r\n			<th scope=\"col\">Phần Thưởng</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align:center\">Bang Hạng 1</td>\r\n			<td style=\"text-align:center\">Bang Chủ</td>\r\n			<td style=\"text-align:center\">\r\n			<p>50 Vạn Đồng Kho&aacute;</p>\r\n\r\n			<p>50 Vạn Bạc Kho&aacute;</p>\r\n\r\n			<p>2 Huyền Tinh Cấp 8</p>\r\n\r\n			<p>Danh Thiệu Thi&ecirc;n Hạ Đệ Nhất Bang Chủ</p>\r\n\r\n			<p><img alt=\"\" src=\"/static/files/uploads/images/372-1.png\" style=\"height:21px; width:170px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Th&agrave;nh Vi&ecirc;n</td>\r\n			<td style=\"text-align:center\">\r\n			<p>25 Vạn Đồng Kho&aacute;</p>\r\n\r\n			<p>25 Vạn Bạc Kho&aacute;</p>\r\n\r\n			<p>1 Huyền Tinh Cấp 8</p>\r\n\r\n			<p>Danh Thiệu Thi&ecirc;n Hạ Đệ Nhất Bang</p>\r\n\r\n			<p><img alt=\"\" src=\"/static/files/uploads/images/347-1.png\" style=\"height:20px; width:143px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align:center\">Bang Hạng 2</td>\r\n			<td style=\"text-align:center\">Bang Chủ</td>\r\n			<td style=\"text-align:center\">\r\n			<p>40 Vạn Đồng Kho&aacute;</p>\r\n\r\n			<p>40 Vạn Bạc Kho&aacute;</p>\r\n\r\n			<p>1 Huyền Tinh Cấp 8</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Th&agrave;nh Vi&ecirc;n</td>\r\n			<td style=\"text-align:center\">\r\n			<p>20 Vạn Đồng Kho&aacute;</p>\r\n\r\n			<p>20 Vạn Bạc Kho&aacute;</p>\r\n\r\n			<p>2 Huyền Tinh Cấp 7</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align:center\">Bang Hạng 3</td>\r\n			<td style=\"text-align:center\">Bang Chủ</td>\r\n			<td style=\"text-align:center\">\r\n			<p>30 Vạn Đồng Kho&aacute;</p>\r\n\r\n			<p>30 Vạn Bạc Kho&aacute;</p>\r\n\r\n			<p>2 Huyền Tinh Cấp 7</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Th&agrave;nh Vi&ecirc;n</td>\r\n			<td style=\"text-align:center\">\r\n			<p>15 Vạn Đồng Kho&aacute;</p>\r\n\r\n			<p>15 Vạn Bạc Kho&aacute;</p>\r\n\r\n			<p>1 Huyền Tinh Cấp 7</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"2\" style=\"text-align:center\">Bang Hạng 4</td>\r\n			<td style=\"text-align:center\">Bang Chủ</td>\r\n			<td style=\"text-align:center\">\r\n			<p>20 Vạn Đồng Kho&aacute;</p>\r\n\r\n			<p>20 Vạn Bạc Kho&aacute;</p>\r\n\r\n			<p>1 Huyền Tinh Cấp 7</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Th&agrave;nh Vi&ecirc;n</td>\r\n			<td style=\"text-align:center\">\r\n			<p>10 Vạn Đồng Kho&aacute;</p>\r\n\r\n			<p>10 Vạn Bạc Kho&aacute;</p>\r\n\r\n			<p>2 Huyền Tinh Cấp 6</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Lưu &Yacute;:</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>Chỉ Những Th&agrave;nh Vi&ecirc;n Tham Gia Thi Đấu Đến Hết Trận Chiến Mới C&oacute; Thể Nhận Thưởng .</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"color:#c0392b\"><strong>BQT Kiếm Thế H&agrave;n Thi&ecirc;n K&iacute;nh B&uacute;t !</strong></span></span><br />\r\n<img alt=\"\" src=\"/static/files/uploads/images/logo/logolon.png\" style=\"height:150px; width:450px\" /></p>\r\n', '2023-03-14 21:48:02', NULL, ''),
(68, 'Võ Lâm Cao Thủ 55/75/95', 'vo-lam-cao-thu-55-75-95', NULL, '<h1>V&otilde; L&acirc;m Cao Thủ Cấp 55</h1>\r\n\r\n<h3><strong>Thời gian:</strong>&nbsp;02h05, 09h35, 15h35, 19h35 v&agrave; 22h35 H&agrave;ng Ng&agrave;y.</h3>\r\n\r\n<h3>Xuất hiện khi m&aacute;y chủ OpenBeta sau&nbsp;<strong>3 ng&agrave;y</strong></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">V&otilde; L&acirc;m Cao Thủ</th>\r\n			<th scope=\"col\">Bản đồ v&agrave; Phần thưởng</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Th&aacute;c Bạt Sơn Uy&ecirc;n (Kim)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;T&acirc;y Th&aacute;p L&acirc;m (Cấp 45).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 500, 5000.</li>\r\n					<li>Vũ kh&iacute; Thiếu L&acirc;m Đao / C&ocirc;n, Thi&ecirc;n Nhẫn Đao / Thương Cấp 50, 60.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>V&acirc;n Tuyết Sơn (Mộc)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;T&acirc;y Bắc Lư Vĩ Đ&atilde;ng (Cấp 45).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 500, 5000.</li>\r\n					<li>Vũ kh&iacute; Thi&ecirc;n Vương Thương / Ch&ugrave;y, Ngũ Độc Đao / Chưởng Cấp 50, 60.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Dương Liễu (Thủy)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;T&acirc;y Long Hổ Huyễn Cảnh (Cấp 45).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 500, 5000.</li>\r\n					<li>Vũ kh&iacute; C&ocirc;n L&ocirc;n Kiếm / Đao, V&otilde; Đang Kiếm / Kh&iacute; Cấp 50, 60.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Vạn L&atilde;o Đi&ecirc;n (Hỏa)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Cửu L&atilde;o Động 2 (Cấp 45).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 500, 5000.</li>\r\n					<li>Vũ kh&iacute; Đường M&ocirc;n Tụ Tiễn / Phi Đao, Nga My Kiếm / Chưởng Cấp 50, 60.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>H&igrave;nh Bổ Đầu (Thổ)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Giữa Yến Tử Ổ (Cấp 45).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 500, 5000.</li>\r\n					<li>Vũ kh&iacute; Minh Gi&aacute;o Kiếm / Ch&ugrave;y, C&aacute;i Bang C&ocirc;n / Chưởng Cấp 50, 60.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Cao Sĩ Hiền (Thổ)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Trong B&aacute;ch Hoa Trận (Cấp 45).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 500, 5000.</li>\r\n					<li>Vũ kh&iacute; Th&uacute;y Y&ecirc;n Kiếm / Đao, Đo&agrave;n Thị Kiếm / Chỉ Cấp 50, 60.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h1>V&otilde; L&acirc;m Cao Thủ Cấp 75</h1>\r\n\r\n<h3><strong>Thời gian:</strong>&nbsp;02h05, 09h35, 15h35, 19h35 v&agrave; 22h35 H&agrave;ng Ng&agrave;y.</h3>\r\n\r\n<h3>Xuất hiện khi m&aacute;y chủ OpenBeta sau&nbsp;<strong>7 ng&agrave;y</strong>.</h3>\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">V&otilde; L&acirc;m Cao Thủ</th>\r\n			<th scope=\"col\">Bản đồ v&agrave; Phần thưởng</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Thần Thương Phương V&atilde;n (Kim)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Ho&agrave;ng Lăng Đo&agrave;n Thị (Cấp 75).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 750, 7500.</li>\r\n					<li>Vũ kh&iacute; Thi&ecirc;n Nhẫn Đao / Thương, C&aacute;i Bang C&ocirc;n / Chưởng Cấp 70, 80 c&oacute; thuộc t&iacute;nh&nbsp;<strong>Thời gian g&acirc;y bỏng</strong>.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Triệu Ứng Ti&ecirc;n (Mộc)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;M&ecirc; Cung Sa Mạc (Cấp 75).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 750, 7500.</li>\r\n					<li>Vũ kh&iacute; Thi&ecirc;n Vương Thương / Ch&ugrave;y, Thiếu L&acirc;m Đao / C&ocirc;n Cấp 70, 80 c&oacute; thuộc t&iacute;nh&nbsp;<strong>Thời gian thọ thương</strong>.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Hương Ngọc Ti&ecirc;n (Thủy)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Cửu Nghi Kh&ecirc; (Cấp 75).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 750, 7500.</li>\r\n					<li>Vũ kh&iacute; V&otilde; Đang Kh&iacute; / Kiếm, C&ocirc;n L&ocirc;n Kiếm / Đao Cấp 70, 80 c&oacute; thuộc t&iacute;nh&nbsp;<strong>Thời gian g&acirc;y cho&aacute;ng</strong>.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Man Tăng Bất Giới (Hỏa)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Thục Cương Sơn (Cấp 75).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 750, 7500.</li>\r\n					<li>Vũ kh&iacute; Nga My Kiếm / Chưởng, Đo&agrave;n Thị Kiếm / Chỉ, Th&uacute;y Y&ecirc;n Kiếm / Đao Cấp 70, 80 c&oacute; thuộc t&iacute;nh&nbsp;<strong>Thời gian l&agrave;m chậm</strong>.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Nam Qu&aacute;ch Nho (Thổ)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;K&ecirc; Qu&aacute;n Động (Cấp 75).</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n					<li>Bạc 750, 7500.</li>\r\n					<li>Vũ kh&iacute; Đường M&ocirc;n Tụ Tiễn / Bẫy, Ngũ Độc Đao / Chưởng, Minh Gi&aacute;o Kiếm / Ch&ugrave;y Cấp 70, 80 c&oacute; thuộc t&iacute;nh&nbsp;<strong>Thời gian suy yếu</strong>.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h1>V&otilde; L&acirc;m Cao Thủ Cấp 95</h1>\r\n\r\n<h3><strong>Thời gian:</strong>&nbsp;15h35, 19h35 v&agrave; 22h35 H&agrave;ng Ng&agrave;y.</h3>\r\n\r\n<h3>Xuất hiện khi m&aacute;y chủ mở giới hạn cấp&nbsp;<strong>99</strong>.</h3>\r\n\r\n<h3>Sử dụng lệnh b&agrave;i v&otilde; l&acirc;m cao thủ gia tăng danh vọng, d&ugrave;ng để mua&nbsp;<a href=\"/vu-khi-dac-che-tho-thuong-suy-yeu.html\"><span style=\"color:#c0392b\">Vũ kh&iacute; Đặc Chế L&acirc;m An</span></a><span style=\"color:#c0392b\">.</span></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">V&otilde; L&acirc;m Cao Thủ</th>\r\n			<th scope=\"col\">Bản đồ v&agrave; Phần thưởng</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Như Tiểu Th&uacute;y (Kim)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Sắc Lặc Xuy&ecirc;n (Phong Kiếm) v&agrave; Ngẫu nhi&ecirc;n c&aacute;c bản đồ 95 (Huyết Kiếm)</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Lệnh b&agrave;i V&otilde; L&acirc;m Cao Thủ Kim, Mộc, Thủy, Hỏa (Tỉ lệ ra cao nhất), Thổ</li>\r\n					<li>Bạc 1000, 10000.</li>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Trương Thiện Đức (Mộc)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Mi&ecirc;u Lĩnh (Phong Kiếm) v&agrave; Ngẫu nhi&ecirc;n c&aacute;c bản đồ 95 (Huyết Kiếm)</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Lệnh b&agrave;i V&otilde; L&acirc;m Cao Thủ Kim (Tỉ lệ ra cao nhất), Mộc, Thủy, Hỏa, Thổ</li>\r\n					<li>Bạc 1000, 10000.</li>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Giả Dật Sơn (Thủy)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện (Ngẫu Nhi&ecirc;n):</strong>&nbsp;Vũ Di Sơn (Phong Kiếm) v&agrave; Ngẫu nhi&ecirc;n c&aacute;c bản đồ 95 (Huyết Kiếm)</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Lệnh b&agrave;i V&otilde; L&acirc;m Cao Thủ Kim, Mộc, Thủy, Hỏa, Thổ (Tỉ lệ ra cao nhất)</li>\r\n					<li>Bạc 1000, 10000.</li>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>&Ocirc; Sơn Th&agrave;nh (Hỏa)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Phong Đ&ocirc; Quỷ Th&agrave;nh (Phong Kiếm) v&agrave; Ngẫu nhi&ecirc;n c&aacute;c bản đồ 95 (Huyết Kiếm)</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Lệnh b&agrave;i V&otilde; L&acirc;m Cao Thủ Kim, Mộc, Thủy (Tỉ lệ ra cao nhất), Hỏa, Thổ</li>\r\n					<li>Bạc 1000, 10000.</li>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align:center\"><strong>Trần V&ocirc; Mệnh (Thổ)</strong></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><strong>Bản đồ xuất hiện:</strong>&nbsp;Gia Dụ Quan (Phong Kiếm) v&agrave; Ngẫu nhi&ecirc;n c&aacute;c bản đồ 95 (Huyết Kiếm)</li>\r\n				<li><strong>Phần thưởng (Ngẫu nhi&ecirc;n):</strong>\r\n				<ul>\r\n					<li>Lệnh b&agrave;i V&otilde; L&acirc;m Cao Thủ Kim, Mộc (Tỉ lệ ra cao nhất), Thủy, Hỏa, Thổ</li>\r\n					<li>Bạc 1000, 10000.</li>\r\n					<li>Huyền Tinh 3, 4, 5, 6, 7, 8.</li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2023-03-14 21:48:30', NULL, ''),
(72, 'Xử Phạt Hành Vi Post Điểm Tống Kim', 'xu-phat-hanh-vi-post-diem-tong-kim', NULL, '<h1>Xử Phạt&nbsp;&nbsp;(POST Điểm) Tống Kim</h1>\r\n\r\n<h2><strong>T&ugrave;y theo số lần bị xử l&yacute; vi phạm từng lần, cụ thể:</strong></h2>\r\n\r\n<h3><strong>Lần 1:</strong> &nbsp;Cấm tham gia Tống Kim trong v&ograve;ng 24h kể từ thời điểm vi phạm</h3>\r\n\r\n<h3><strong>Lần 2:</strong> &nbsp;Đưa account l&ecirc;n Đảo Nguy&ecirc;n trong v&ograve;ng 24 giờ, đồng nghĩa với việc kh&ocirc;ng thể hoạt động</h3>\r\n\r\n<h3>Nếu c&oacute; khiếu nại, bạn c&oacute; thể li&ecirc;n hệ&nbsp; <strong><span style=\"color:#c0392b\">FANPAGE </span></strong>để khiếu nại v&agrave; y&ecirc;u cầu xem lịch sử h&agrave;nh vi tống kim.</h3>\r\n\r\n<h2><strong>Hạ s&aacute;t LI&Ecirc;N TIẾP 1 người 4 mạng kh&ocirc;ng ngắt qu&atilde;ng</strong>&nbsp;l&agrave; g&igrave; ?</h2>\r\n\r\n<h3>L&agrave; h&agrave;nh động người A giết người B li&ecirc;n tiếp 4 lần</h3>\r\n\r\n<h2><em>V&iacute; dụ:</em></h2>\r\n\r\n<h3>Người chơi A giết người chơi B</h3>\r\n\r\n<h3>Tiếp tục giết người chơi B</h3>\r\n\r\n<h3>Lại tiếp tục giết người chơi B</h3>\r\n\r\n<h3>Rồi tiếp tục giết người chơi B</h3>\r\n\r\n<h3>Tổng người chơi A giết người chơi B 4 lần li&ecirc;n tiếp -&gt; h&agrave;nh động n&agrave;y&nbsp;<strong>được t&iacute;nh</strong>&nbsp;l&agrave;&nbsp;<strong>post mạng (buff điểm)</strong></h3>\r\n\r\n<h2><em><em>V&iacute; dụ:</em></em></h2>\r\n\r\n<h3>Người chơi A giết người chơi B</h3>\r\n\r\n<h3>Tiếp tục giết người chơi B</h3>\r\n\r\n<h3>Lại tiếp tục giết&nbsp;<strong>người chơi C</strong></h3>\r\n\r\n<h3>Rồi tiếp tục giết người chơi B</h3>\r\n\r\n<h3>Tổng người chơi A giết người chơi B 2 lần li&ecirc;n tiếp, nhưng sau đ&oacute; giết người C 1 lần rồi mới giết người chơi B 1 lần -&gt; h&agrave;nh động n&agrave;y&nbsp;<strong>kh&ocirc;ng được t&iacute;nh</strong>&nbsp;l&agrave;&nbsp;<strong>post mạng (buff điểm)</strong></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2023-03-14 21:48:09', NULL, ''),
(73, 'Hướng Dẫn Tải Và Cài Đặt Game', 'huong-dan-tai-va-cai-dat-game', NULL, '<h1>Đường Dẫn Tải V&agrave; Phi&ecirc;n Bản.</h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; <span style=\"font-size:16px\"><span style=\"color:#c0392b\">&nbsp; <strong>Bản Đầy Đủ (7GB) - Khuyễn Kh&iacute;ch D&ugrave;ng Bản N&agrave;y.</strong>&nbsp;</span></span> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style=\"color:#c0392b\"><span style=\"font-size:16px\"><strong>Bản Cập Nhật (39MB) M&aacute;y T&iacute;nh Đ&atilde; C&oacute; Folder Pak</strong></span></span></p>\r\n\r\n<table align=\"left\" border=\"1\" cellpadding=\"1\" cellspacing=\"5\" style=\"width:420px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Bản Đầy Đủ (7GB)</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h4><strong>Bước 1:</strong>&nbsp;Tải FULL Nếu Chưa C&oacute; Bản VNG</h4>\r\n\r\n			<p style=\"text-align:center\"><a href=\"https://drive.google.com/file/d/1z_j6XHQebQ_tj3CesY1N-STRo7n0KEkN/view?usp=sharing\" style=\"height:219px; width:213px\"><input alt=\"\"class=\"downloadonpostfull\" src=\"/static/files/uploads/images/btn-download-transparent.png\" type=\"image\" /></a></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h4><strong>Bước 2 :</strong> Giải N&eacute;n File Vừa Tải Về.</h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h4><strong>Bước 3 :</strong> Tiến h&agrave;nh Chạy AutoUpdate.</h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h4><strong>Bước 4 : </strong>V&agrave;o Game V&agrave; Chinh Chiến</h4>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"right\" border=\"1\" cellpadding=\"1\" cellspacing=\"5\" style=\"width:420px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Bản Cập Nhật (39MB)</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h4><strong>Bước 1:</strong>&nbsp;Tải PATCH nếu m&aacute;y đ&atilde; c&oacute; bản VNG</h4>\r\n\r\n			<p style=\"text-align:center\"><a href=\"https://drive.google.com/file/d/1VZCb4j9egVEnZZYo5zIeHDXhKfQ15wUC/view?usp=sharing\" style=\"height:219px; width:213px\"><input alt=\"\" class=\"downloadonpostpath\"src=\"/static/files/uploads/images/btn-download-transparent.png\" type=\"image\" /></a></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h4><strong>Bước 2 :</strong> Giải N&eacute;n File Vừa Tải Về</h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h4><strong>Bước 3 :</strong> Copy To&agrave;n Bộ File Vừa Giải N&eacute;n V&agrave;o Folder VNG</h4>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h4><strong>Bước 4 :</strong> Chạy AutoUpdate V&agrave; V&agrave;o Game</h4>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nếu Bị Ph&aacute;t Hiện Lỗi Vui L&ograve;ng Tắt AntiVirus Hoặc Li&ecirc;n Hệ FanPage Để Được Hỗ Trợ</p>\r\n\r\n<p>BQT K&iacute;nh B&uacute;t</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2023-03-14 21:52:56', NULL, ''),
(76, 'Bí Ẩn Đại Mạc - Lâu Lan Cổ Thành', 'bi-an-dai-mac-lau-lan-co-thanh', NULL, '<h1>Thời gian v&agrave; điều kiện tham gia</h1>\r\n\r\n<h2><strong>Điều kiện tham gia đối với c&aacute; nh&acirc;n:</strong></h2>\r\n\r\n<h3>Nh&acirc;n vật đạt&nbsp;<strong>đẳng cấp 100</strong>&nbsp;v&agrave; đ&atilde; gia nhập m&ocirc;n ph&aacute;i.</h3>\r\n\r\n<h3><strong>Trang bị Phi Phong Hỗn Thi&ecirc;n</strong>&nbsp;trở l&ecirc;n.</h3>\r\n\r\n<h2><strong>Điều kiện tham gia đối với tổ đội:</strong></h2>\r\n\r\n<h3><strong>Tất cả c&aacute;c th&agrave;nh vi&ecirc;n trong tổ đội đều phải thỏa điều kiện</strong>: đẳng cấp 100 trở l&ecirc;n, đ&atilde; gia nhập m&ocirc;n ph&aacute;i v&agrave; c&oacute; trang bị Phi Phong Hỗn Thi&ecirc;n th&igrave; đội trưởng mới c&oacute; thể b&aacute;o danh.</h3>\r\n\r\n<h3>Bất cứ th&agrave;nh vi&ecirc;n n&agrave;o trong tổ đội hoặc c&aacute; nh&acirc;n kh&ocirc;ng hội đủ tất cả c&aacute;c điều kiện tr&ecirc;n đều kh&ocirc;ng thể tham gia t&iacute;nh năng.</h3>\r\n\r\n<h2><strong>Thời gian diễn ra hoạt động: Từ 15h00 đến 24h00 h&agrave;ng ng&agrave;y.</strong></h2>\r\n\r\n<h3>Mỗi nh&acirc;n vật chỉ được v&agrave;o L&acirc;u Lan Cổ Th&agrave;nh tối đa 02 giờ/ng&agrave;y. Sau khi hết 02 giờ, nh&acirc;n vật sẽ tự động được đưa ra khỏi bản đồ.</h3>\r\n\r\n<h1>C&aacute;ch thức tham gia.</h1>\r\n\r\n<h3>C&aacute; nh&acirc;n hoặc tổ đội (đội trưởng) thỏa điều kiện đến b&aacute;o danh với NPC Yến Tiểu L&acirc;u tại Phượng Tường Phủ để v&agrave;o L&acirc;u Lan.</h3>\r\n\r\n<h3>Tất cả nh&acirc;n vật khi v&agrave;o L&acirc;u Lan đều&nbsp;<strong>biến th&agrave;nh &quot;Người Thần B&iacute;&quot; v&agrave; chuyển sang chế độ PK</strong>&nbsp;(kể cả hảo hữu, Gia tộc hay Bang hội). Chỉ những th&agrave;nh vi&ecirc;n c&ugrave;ng tổ đội mới c&oacute; thể thấy nhau.</h3>\r\n\r\n<h3>Đả thương qu&aacute;i trong bản đồ để thu thập Bạc, Huyền tinh, b&aacute;u vật... may mắn hơn c&oacute; thể nhặt được&nbsp;<strong>vũ kh&iacute; Thần Binh +16</strong>&nbsp;(ngũ h&agrave;nh v&agrave; thuộc t&iacute;nh của Thần Binh theo hệ ph&aacute;i của nh&acirc;n vật).</h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/14112011_thanbinh.jpg\" style=\"height:550px; width:356px\" /></p>\r\n\r\n<h3>B&ecirc;n cạnh Thần Binh sẽ xuất hiện&nbsp;<strong>Lạc Thần Ch&ugrave;y</strong>: vật phẩm để vứt bỏ Thần Binh (khi muốn trao lại cho đồng đội, đi v&agrave;o khu an to&agrave;n hoặc muốn vứt bỏ Thần Binh).</h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/14112011_lacthanchuy.jpg\" style=\"height:214px; width:263px\" /></p>\r\n\r\n<h3>Mỗi tổ đội chỉ c&oacute; một th&agrave;nh vi&ecirc;n được sử dụng Thần Binh. Khi nh&acirc;n vật giữ Thần Binh bị trọng thương, Thần Binh sẽ tự động rơi xuống đất.</h3>\r\n\r\n<h1>Thu thập b&aacute;u vật</h1>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">B&aacute;u Vật L&acirc;u Lan</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/14112011_bichngoccap1.jpg\" style=\"height:217px; width:276px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/14112011_linhtinhcap2.jpg\" style=\"height:215px; width:270px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/14112011_nhatucap1.jpg\" style=\"height:216px; width:260px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/14112011_bachngoccap2.jpg\" style=\"height:212px; width:281px\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Ngo&agrave;i Bạc, Huyền tinh hay Thần Binh may mắn, khi đả thương qu&aacute;i trong L&acirc;u L&acirc;n, nh&acirc;n vật c&ograve;n thu thập được c&aacute;c b&aacute;u vật để đổi m&atilde;nh trang bị Bạn Đồng H&agrave;nh.</h3>\r\n\r\n<h3>D&ugrave;ng vật b&aacute;u L&acirc;u Lan kết hợp với Kim Nguy&ecirc;n Bảo&nbsp;để đổi mảnh trang bị Bạn Đồng H&agrave;nh:</h3>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:600px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">T&ecirc;n Vật Phẩm</th>\r\n			<th scope=\"col\">C&ocirc;ng Thức</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/vkdh.jpg\" style=\"height:164px; width:296px\" /></td>\r\n			<td style=\"text-align:center\">\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan B&iacute;ch Ngọc</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Tinh Linh</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Nh&atilde; Từ</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Bạch Ngọc</p>\r\n\r\n			<p>1 Kim Nguy&ecirc;n Bảo</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/aodh.jpg\" style=\"height:162px; width:296px\" /></td>\r\n			<td style=\"text-align:center\">\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan B&iacute;ch Ngọc</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Tinh Linh</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Nh&atilde; Từ</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Bạch Ngọc</p>\r\n\r\n			<p>1 Kim Nguy&ecirc;n Bảo</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/nhandh.jpg\" style=\"height:165px; width:295px\" /></td>\r\n			<td style=\"text-align:center\">\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan B&iacute;ch Ngọc</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Tinh Linh</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Nh&atilde; Từ</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Bạch Ngọc</p>\r\n\r\n			<p>1 Kim Nguy&ecirc;n Bảo</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/phudh.jpg\" style=\"height:166px; width:295px\" /></td>\r\n			<td style=\"text-align:center\">\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan B&iacute;ch Ngọc</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Tinh Linh</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Nh&atilde; Từ</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Bạch Ngọc</p>\r\n\r\n			<p>1 Kim Nguy&ecirc;n Bảo</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/taydh.jpg\" style=\"height:163px; width:295px\" /></td>\r\n			<td style=\"text-align:center\">\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan B&iacute;ch Ngọc</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Tinh Linh</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Nh&atilde; Từ</p>\r\n\r\n			<p>1 Vật B&aacute;u L&acirc;u Lan Bạch Ngọc</p>\r\n\r\n			<p>1 Kim Nguy&ecirc;n Bảo</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:20px\"><span style=\"color:#c0392b\"><strong>BQT Kiếm Thế H&agrave;n Thi&ecirc;n K&iacute;nh B&uacute;t !</strong></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2023-03-14 21:47:59', NULL, '');
INSERT INTO `blog_posts_seo` (`postID`, `postTitle`, `postSlug`, `postDesc`, `postCont`, `postDate`, `postTags`, `postImage`) VALUES
(77, 'Sự Kiện Tích Luỹ Nạp Thẻ', 'su-kien-tich-luy-nap-the', NULL, '<p>Nhằm Gi&uacute;p Qu&yacute; Nh&acirc;n Sỹ Dễ D&agrave;ng B&ocirc;n Tẩu Trong Thế Giới <span style=\"color:#c0392b\"><strong>Ch&acirc;n Long Kiếm </strong></span>Hơn BQT <span style=\"color:#3300ff\"><strong>Kiếm Thế H&agrave;n Thi&ecirc;n</strong></span> Xin Được Hỗ Trợ C&aacute;c Mốc Nạp Tại M&aacute;y Chủ <span style=\"color:#c0392b\"><strong>Ch&acirc;n Long Kiếm</strong></span> Như Sau.</p>\r\n\r\n<h1>Thời Gian V&agrave; Đối Tượng.</h1>\r\n\r\n<h2><strong>Thời Gian.</strong></h2>\r\n\r\n<h3>Tất Cả Mọi Giờ Bắt Đầu Từ Khi M&aacute;y Chủ <strong>Ch&acirc;n Long Kiếm</strong> Open</h3>\r\n\r\n<h2><strong>Đối Tượng &amp; Quy Tắc.</strong></h2>\r\n\r\n<h3>To&agrave;n Thể Nh&acirc;n Sỹ Tại M&aacute;y Chủ <strong>CH&Acirc;N LONG KIẾM&nbsp;</strong></h3>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Lưu &Yacute; :</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>Trong Suốt Qu&aacute; Tr&igrave;nh Sự Kiện To&agrave;n Bộ Lượt Nạp Thẻ Từ Thẻ C&agrave;o Hoặc Chuyển Khoản ATM, V&iacute; Điện Tử Đều Được Cộng Dồn Đến Mốc Cao Nhất Của Phần Thưởng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><strong>Tỉ Lệ Nạp Thẻ Tại M&aacute;y Chủ <span style=\"color:#ff0000\">CH&Acirc;N LONG KIẾM</span>.</strong></h2>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:80%\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Mệnh Gi&aacute;</th>\r\n			<th scope=\"col\">ATM - V&iacute; Điện Tử</th>\r\n			<th scope=\"col\">Thẻ C&agrave;o</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>100.000 VNĐ</strong></td>\r\n			<td style=\"text-align:center\"><strong>120 Vạn Đồng Thường</strong></td>\r\n			<td style=\"text-align:center\"><strong>100 vạn Đồng Thường</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>200.000 VNĐ</strong></td>\r\n			<td style=\"text-align:center\"><strong>240&nbsp;Vạn Đồng Thường</strong></td>\r\n			<td style=\"text-align:center\"><strong>200 Vạn Đồng Thường</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>500.000 VNĐ</strong></td>\r\n			<td style=\"text-align:center\"><strong>600 Vạn Đồng Thường</strong></td>\r\n			<td style=\"text-align:center\"><strong>500 Vạn Đồng Thường</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\"><strong>1.000.000 VNĐ</strong></td>\r\n			<td style=\"text-align:center\"><strong>1200 Vạn Đồng Thường</strong></td>\r\n			<td style=\"text-align:center\"><strong>1000 Vạn Đồng Thường</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\" style=\"text-align:center\"><span style=\"color:#ff0000\"><strong>Từ 1.000.000 VNĐ Trở L&ecirc;n Chuyển Khoản Đều Được Khuyễn Mại 20%</strong></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h1>Phần Thưởng T&iacute;ch Luỹ&nbsp;C&aacute;c Mốc Nạp Thẻ.</h1>\r\n\r\n<h2><strong>Xuất hiện&nbsp;đặt quyền lần đầu ti&ecirc;n xuất hiện v&agrave; duy nhất tr&ecirc;n to&agrave;n c&otilde;i Kiếm Thế. Khi t&iacute;ch lũy đủ mốc sẽ c&oacute; quyền chọn&nbsp;bằng hữu để tặng đồng kh&oacute;a, mỗi nh&acirc;n vật sẽ chỉ được nhận 1 lần duy nhất.</strong></h2>\r\n\r\n<h2><strong>Khi t&iacute;ch lũy đủ c&aacute;c mốc <span style=\"color:#0000ff\">10.000 vạn</span>, <span style=\"color:#0000ff\">30.000 vạn</span>, <span style=\"color:#0000ff\">60.000 vạn</span> th&igrave; sẽ được th&ecirc;m <span style=\"color:#ff0000\">1 lần nhận đồng kh&oacute;a</span> từ bằng hữu trao tặng.</strong></h2>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:80%\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Mốc Nạp</th>\r\n			<th scope=\"col\">Phần Thưởng</th>\r\n			<th scope=\"col\">Ghi Ch&uacute;</th>\r\n			<th scope=\"col\">Đặc Quyền</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:center\">50 Vạn Đồng</td>\r\n			<td style=\"text-align:center\">Lệnh b&agrave;i mở rộng rương lv1<br />\r\n			T&uacute;i phi phượng 24 &ocirc;</td>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/napthe/50van.png\" style=\"height:39px; width:77px\" /></td>\r\n			<td style=\"text-align:center\"><strong>Kh&ocirc;ng</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">200 Vạn Đồng</td>\r\n			<td style=\"text-align:center\">Lệnh b&agrave;i mở rộng rương lv2<br />\r\n			T&uacute;i Thi&ecirc;n T&agrave;m 24 &ocirc;</td>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/napthe/100van.png\" style=\"height:39px; width:77px\" /></td>\r\n			<td style=\"text-align:center\"><strong>Kh&ocirc;ng</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">300 Vạn Đồng</td>\r\n			<td style=\"text-align:center\">Lệnh b&agrave;i mở rộng rương lv3<br />\r\n			T&uacute;i B&agrave;n Long 24 &ocirc;</td>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/napthe/300van.png\" style=\"height:39px; width:77px\" /></td>\r\n			<td style=\"text-align:center\"><strong>Kh&ocirc;ng</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">500 Vạn Đồng</td>\r\n			<td style=\"text-align:center\">\r\n			<p>Ngựa X&iacute;ch Thố<br />\r\n			Kh&aacute;ng tất cả 30<br />\r\n			Ph&aacute;t huy lực tấn c&ocirc;ng cơ bản 5%<br />\r\n			T&agrave;i ph&uacute; 500</p>\r\n			</td>\r\n			<td style=\"text-align:center\">\r\n			<p><img alt=\"\" src=\"/static/files/uploads/images/napthe/500van.png\" style=\"height:39px; width:39px\" /></p>\r\n\r\n			<p>Danh hiệu : Nh&agrave; T&agrave;i Trợ Đồng.</p>\r\n\r\n			<p><img alt=\"\" src=\"/static/files/uploads/images/napthe/nhataitrodong.png\" style=\"height:265px; width:203px\" /></p>\r\n			</td>\r\n			<td style=\"text-align:center\"><strong>Kh&ocirc;ng</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">1000 Vạn đồng</td>\r\n			<td style=\"text-align:center\">\r\n			<p>1 lần chuyển cường h&oacute;a trang bị tại<br />\r\n			NPC D&atilde; Luyện Đại Sư</p>\r\n\r\n			<p>200 vạn đồng kh&oacute;a</p>\r\n			</td>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/napthe/kim.png\" style=\"height:40px; width:40px\" /></td>\r\n			<td style=\"text-align:center\">\r\n			<p><span style=\"color:#ff0000\"><strong>Mua TLHL Với Gi&aacute; Khuyễn M&atilde;i 40%<br />\r\n			Kh&ocirc;ng Cần Uy Danh.</strong></span></p>\r\n\r\n			<p><strong><span style=\"color:#d35400\">G&oacute;i Ưu Đ&atilde;i H&agrave;ng Ng&agrave;y :<br />\r\n			- 1 Huyền Tinh Cấp 5 (Kho&aacute;)<br />\r\n			- 40 Tiền Du Long<br />\r\n			Gi&aacute; : 10 Vạn Đồng<br />\r\n			Mỗi ng&agrave;y Mua 1 Lần</span></strong></p>\r\n\r\n			<p><strong><span style=\"color:#c0392b\">Khuyễn M&atilde;i Th&ecirc;m 10% KNB<br />\r\n			Gi&aacute; Trị Nạp</span></strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">2000 Vạn đồng</td>\r\n			<td style=\"text-align:center\">\r\n			<p>1 lần chuyển cường h&oacute;a trang bị tại<br />\r\n			NPC D&atilde; Luyện Đại Sư</p>\r\n\r\n			<p>Một mặt nạ h&agrave;ng long:<br />\r\n			Kh&aacute;ng tất cả 50<br />\r\n			Kinh nghiệm giết địch tăng 20%<br />\r\n			t&agrave;i ph&uacute; 1500</p>\r\n			</td>\r\n			<td style=\"text-align:center\"><img alt=\"\" src=\"/static/files/uploads/images/napthe/1000van.png\" style=\"height:39px; width:39px\" /></td>\r\n			<td style=\"text-align:center\">\r\n			<p><span style=\"color:#660099\"><strong>Luyện h&oacute;a nhanh mật tịch.</strong></span></p>\r\n\r\n			<p><br />\r\n			<strong><span style=\"color:#0000ff\">Được Chọn 1 Vị Bằng Hữu<br />\r\n			Để Nhận Số Lượng KM N&agrave;y<br />\r\n			Kh&ocirc;ng Thể Nhận Cho Bản Th&acirc;n<br />\r\n			200 Vạn Đồng Kho&aacute;.</span></strong></p>\r\n\r\n			<p><strong><span style=\"color:#c0392b\">Khuyễn M&atilde;i Th&ecirc;m 10% KNB<br />\r\n			Gi&aacute; Trị Nạp</span></strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">5000 Vạn đồng</td>\r\n			<td style=\"text-align:center\">\r\n			<p>2 lần chuyển cường h&oacute;a trang bị tại<br />\r\n			NPC D&atilde; Luyện Đại Sư</p>\r\n\r\n			<p>Ngựa Lăng Thi&ecirc;n :<br />\r\n			Kh&aacute;ng tất cả 50<br />\r\n			Ph&aacute;t huy lực tấn c&ocirc;ng cơ bản 10%<br />\r\n			T&agrave;i ph&uacute; 1000</p>\r\n			</td>\r\n			<td style=\"text-align:center\">\r\n			<p><img alt=\"\" src=\"/static/files/uploads/images/napthe/5000van.png\" style=\"height:39px; width:39px\" /><br />\r\n			Danh hiệu : Nh&agrave; T&agrave;i Trợ Bạc.</p>\r\n\r\n			<p><img alt=\"\" src=\"/static/files/uploads/images/napthe/nhataitrobac.png\" style=\"height:243px; width:208px\" /></p>\r\n			</td>\r\n			<td style=\"text-align:center\">\r\n			<p><strong><span style=\"color:#e67e22\">G&oacute;i Ưu Đ&atilde;i H&agrave;ng Ng&agrave;y :<br />\r\n			- 1 Huyền Tinh Cấp 6 (Kho&aacute;)<br />\r\n			- 80 Tiền Du Long<br />\r\n			Gi&aacute; : 20 Vạn Đồng<br />\r\n			Mỗi ng&agrave;y Mua 1 Lần</span></strong></p>\r\n\r\n			<p><span style=\"color:#003399\"><span style=\"font-size:16px\"><strong><span style=\"background-color:#ffffcc\">Đặc Quyền V&agrave;o Tống Kim</span><br />\r\n			<span style=\"background-color:#ffffcc\">Kh&ocirc;ng Bị Hạn Chế Ch&ecirc;nh lệch 2 B&ecirc;n</span></strong></span></span></p>\r\n\r\n			<p><strong><span style=\"color:#c0392b\">Khuyễn M&atilde;i Th&ecirc;m 15% KNB<br />\r\n			Gi&aacute; Trị Nạp</span></strong></p>\r\n\r\n			<p><span style=\"color:#ff0000\"><strong>Mua TLHL Với Gi&aacute; Khuyễn M&atilde;i 80%<br />\r\n			Kh&ocirc;ng Cần Uy Danh</strong></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">10.000 Vạn đồng</td>\r\n			<td style=\"text-align:center\">\r\n			<p>3 lần chuyển cường h&oacute;a trang bị tại<br />\r\n			NPC D&atilde; Luyện Đại Sư</p>\r\n\r\n			<p>Ngựa Ức V&acirc;n :<br />\r\n			Kh&aacute;ng tất cả 